import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Navbar from './common/Navbar';
import likeIcon from '../assets/like-icon.png'; // Иконка "лайк"
import likedIcon from '../assets/liked-icon.png'; // Иконка "лайкнутого"

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [publicPalettes, setPublicPalettes] = useState([]);
  const [publicArts, setPublicArts] = useState([]);
  const [activeCategory, setActiveCategory] = useState('palettes'); // Для переключения между категориями

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }

    // Загрузка публичных палитр
    axios.get('http://localhost:8080/palettes/public')
      .then(response => setPublicPalettes(response.data))
      .catch(error => console.error('Error fetching public palettes:', error));

    // Загрузка публичных артов
    axios.get('http://localhost:8080/arts/public')
      .then(response => setPublicArts(response.data))
      .catch(error => console.error('Error fetching public arts:', error));
  }, []);

  const handleLike = (paletteId) => {
    // Обработка лайка палитры
    axios.post(`http://localhost:8080/palettes/${paletteId}/like`, {}, {
      withCredentials: true,
    })
    .then(() => {
      // Логика обновления лайков на UI
    })
    .catch((error) => console.error('Error liking palette:', error));
  };

  return (
    <div className="min-h-screen bg-primary">
      <Navbar
        username={username}
        isAuthenticated={isAuthenticated}
        handleLogout={() => {
          sessionStorage.removeItem('authToken');
          sessionStorage.removeItem('username');
          window.location.href = '/login';
        }}
        onHomePage={true}
      />

      {/* Контент */}
      <div className="pt-16"> {/* Отступ сверху, чтобы контент не наезжал на Navbar */}
        <div className="container mx-auto py-8">

          {/* Переключатель между категориями */}
          <div className="text-center mb-8">
            <button
              onClick={() => setActiveCategory('palettes')}
              className={`px-4 py-2 mx-2 ${activeCategory === 'palettes' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
            >
              Palettes
            </button>
            <button
              onClick={() => setActiveCategory('arts')}
              className={`px-4 py-2 mx-2 ${activeCategory === 'arts' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
            >
              Arts
            </button>
          </div>

          {/* Содержимое для палитр */}
          {activeCategory === 'palettes' && (
            <>
              <h2 className="text-2xl font-bold text-secondary mb-4">Public Palettes</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
                {publicPalettes.length > 0 ? (
                  publicPalettes.map((palette, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                      <h3 className="text-lg font-bold mb-2 text-white">{palette.name}</h3>
                      <p className="text-sm text-white mb-2">by {palette.user?.username ? (
                        <Link to={`/users/${palette.user.username}`} className="underline">
                          {palette.user.username}
                        </Link>
                      ) : 'Unknown'}</p>
                      <div className="grid grid-cols-4 gap-0 mb-4"> {/* Убрали отступы между квадратиками */}
                        {palette.colors.map((color, idx) => (
                          <div
                            key={idx}
                            className="w-8 h-8"
                            style={{ backgroundColor: color }}
                          />
                        ))}
                      </div>
                      <div className="flex items-center justify-between">
                        <button onClick={() => handleLike(palette.id)}>
                          <img src={likeIcon} alt="Like" className="h-6 w-6 mr-2" />
                        </button>
                        <span className="text-white text-xs">{palette.likes} Likes</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-white">No public palettes available.</p>
                )}
              </div>
            </>
          )}

          {/* Содержимое для артов */}
          {activeCategory === 'arts' && (
            <>
              <h2 className="text-2xl font-bold text-secondary mb-4">Public Arts</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
                {publicArts.length > 0 ? (
                  publicArts.map((art, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                      <h3 className="text-lg font-bold mb-2 text-white">{art.name}</h3>
                      <p className="text-sm text-white mb-2">by {art.user?.username ? (
                        <Link to={`/users/${art.user.username}`} className="underline">
                          {art.user.username}
                        </Link>
                      ) : 'Unknown'}</p>
                      <div
                        className="grid"
                        style={{
                          gridTemplateColumns: `repeat(${art.width}, 1fr)`,
                          gap: '0px',
                        }}
                      >
                        {art.pixels.map((color, idx) => (
                          <div
                            key={idx}
                            style={{
                              backgroundColor: color,
                              width: '20px',
                              height: '20px',
                            }}
                          />
                        ))}
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-white">No public arts available.</p>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
