import React, { useState, useRef, useEffect } from 'react';
import { ChromePicker } from 'react-color';  // Цветовое колесо
import Navbar from './common/Navbar';  // Верхняя панель

const CreatePalette = () => {
  const [selectedColor, setSelectedColor] = useState('#fff');
  const [isPickerVisible, setIsPickerVisible] = useState(false);
  const [currentSquare, setCurrentSquare] = useState(null);
  const [position, setPosition] = useState({ top: 0, left: 0 });
  const squares = new Array(16).fill(''); // 16 квадратов для примера

  const pickerRef = useRef(null);

  // Скрываем color picker при клике вне его области
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        setIsPickerVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Обработчик клика по квадрату
  const handleSquareClick = (index, event) => {
    setCurrentSquare(index);
    setIsPickerVisible(true);
    // Устанавливаем позицию рядом с квадратом
    const rect = event.target.getBoundingClientRect();
    setPosition({ top: rect.bottom, left: rect.left });
  };

  // Обновляем цвет
  const handleColorChange = (color) => {
    setSelectedColor(color.hex);
  };

  return (
    <div className="min-h-screen bg-primary p-4">
      {/* Верхняя панель */}
      <Navbar />

      <div className="grid grid-cols-4 gap-4 mt-4">
        {squares.map((_, index) => (
          <div
            key={index}
            className="w-20 h-20 border"
            style={{ backgroundColor: selectedColor }}
            onClick={(e) => handleSquareClick(index, e)}
          >
            {/* Квадрат */}
          </div>
        ))}
      </div>

      {/* Color Picker */}
      {isPickerVisible && (
        <div
          ref={pickerRef}
          style={{
            position: 'absolute',
            top: `${position.top}px`,
            left: `${position.left}px`,
            zIndex: 1000,
          }}
        >
          <ChromePicker color={selectedColor} onChange={handleColorChange} />
        </div>
      )}
    </div>
  );
};

export default CreatePalette;
