import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserProfile = ({ username }) => {
  const [palettes, setPalettes] = useState([]);
  const [currentUser, setCurrentUser] = useState('');

  useEffect(() => {
    // Получение информации о пользователе
    const loggedInUser = sessionStorage.getItem('username');
    setCurrentUser(loggedInUser);

    // Получение палитр пользователя
    axios.get(`/palettes/user/${username}`)
      .then((response) => setPalettes(response.data))
      .catch((error) => console.error(error));
  }, [username]);

  const handleLike = (paletteId) => {
    axios.post(`/palettes/${paletteId}/like`, {}, {
      withCredentials: true
    })
    .then((response) => {
      // Обновление списка палитр после лайка
      setPalettes(palettes.map(palette =>
        palette.id === paletteId ? { ...palette, likes: palette.likes + 1 } : palette
      ));
    })
    .catch((error) => console.error('Error liking palette:', error));
  };

  return (
    <div className="container mx-auto py-8">
      <h1 className="text-3xl font-bold mb-6">Profile of {username}</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {palettes.map((palette) => (
          <div key={palette.id} className="p-4 border rounded-lg shadow">
            <h2 className="text-xl font-bold">{palette.name}</h2>
            <div className="grid grid-cols-5 gap-2 mt-4">
              {palette.colors.map((color, index) => (
                <div key={index} className="w-12 h-12 border" style={{ backgroundColor: color }}></div>
              ))}
            </div>
            <div className="flex justify-between items-center mt-4">
              <span>{palette.likes} likes</span>
              {currentUser !== username && (
                <button
                  onClick={() => handleLike(palette.id)}
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                  Like
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserProfile;
