import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Navbar from './common/Navbar';
import likeIcon from '../assets/like-icon.png';  // Импортируем иконку лайка

const UserProfile = () => {
  const { username } = useParams();
  const [user, setUser] = useState(null);
  const [palettes, setPalettes] = useState([]);
  const [avatar, setAvatar] = useState(null);
  const [currentUser, setCurrentUser] = useState('');

  useEffect(() => {
    // Загружаем данные пользователя
    axios.get(`http://localhost:8080/users/${username}`)
      .then(response => {
        setUser(response.data);
        setAvatar(`http://localhost:8080${response.data.avatarPath}`);
      })
      .catch(error => console.error('Error fetching user data:', error));

    // Загружаем палитры пользователя
    axios.get(`http://localhost:8080/palettes/user/${username}`)
      .then(response => {
        setPalettes(response.data);
      })
      .catch(error => console.error('Error fetching palettes:', error));

    // Получаем текущего залогиненного пользователя
    const loggedInUser = sessionStorage.getItem('username');
    setCurrentUser(loggedInUser);
  }, [username]);

  if (!user) {
    return <div>Loading...</div>;
  }

  const handleLike = (paletteId) => {
    axios.post(`/palettes/${paletteId}/like`, {}, {
      withCredentials: true
    })
    .then((response) => {
      // Обновляем список палитр после лайка
      setPalettes(palettes.map(palette =>
        palette.id === paletteId ? { ...palette, likes: palette.likes + 1 } : palette
      ));
    })
    .catch((error) => console.error('Error liking palette:', error));
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar />
      <div className="container mx-auto py-8 text-center">
        <img src={avatar} alt="User Avatar" className="rounded-full h-32 w-32 object-cover mx-auto mb-4" />
        <h1 className="text-3xl font-bold text-white">{user.username}</h1>

        <div className="mt-8">
          <h2 className="text-2xl font-semibold text-white mb-4">Palettes</h2>
          {palettes.length > 0 ? (
            <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {palettes.map((palette, index) => (
                <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                  <h3 className="text-lg font-bold mb-2 text-white">{palette.name}</h3>
                  <div className="grid grid-cols-4 gap-2 mb-4">
                    {palette.colors.map((color, idx) => (
                      <div
                        key={idx}
                        className="w-8 h-8 md:w-10 md:h-10 lg:w-12 lg:h-12 rounded border-2 border-gray-300"
                        style={{ backgroundColor: color }}
                      />
                    ))}
                  </div>
                  <div className="flex items-center justify-between">
                    <button
                      onClick={() => handleLike(palette.id)}
                      disabled={currentUser === username}  // Запрещаем лайкать свою палитру
                      className={`flex items-center ${currentUser === username ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105 transition-transform'}`}
                    >
                      <img src={likeIcon} alt="Like" className="h-6 w-6 mr-2" />
                      <span className="text-white">{palette.likes} Likes</span>
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-white">This user has no palettes yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
