import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AccountSettings = () => {
  const [username, setUsername] = useState('');
  const [newUsername, setNewUsername] = useState('');
  const [avatar, setAvatar] = useState(null);  // Для хранения загруженного аватара
  const [message, setMessage] = useState('');

  useEffect(() => {
    const savedUsername = sessionStorage.getItem('username');
    if (savedUsername) {
      setUsername(savedUsername);
      setNewUsername(savedUsername);  // Заполняем форму текущим никнеймом
    }
  }, []);

  // Обработчик изменения аватара
  const handleAvatarChange = (e) => {
    setAvatar(e.target.files[0]);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = sessionStorage.getItem('authToken');  // Берём токен из sessionStorage

      const formData = new FormData();
      formData.append('currentUsername', username);
      formData.append('newUsername', newUsername);
      if (avatar) {
        formData.append('avatar', avatar);  // Добавляем файл аватара
      }

      const response = await axios.put('http://localhost:8080/users/update-username', formData, {
        withCredentials: true,
        headers: {
          'Content-Type': 'multipart/form-data',  // Для отправки файлов
        }
      });

      if (response.status === 200) {
        sessionStorage.setItem('username', newUsername);  // Обновляем username в sessionStorage
        setMessage('Username and avatar updated successfully');
      } else {
        setMessage('Failed to update username or avatar');
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-pixelBg">
      <div className="bg-white shadow-lg rounded-lg p-8 max-w-md w-full">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">Account Settings</h1>

        {message && (
          <p className="text-center mb-4 text-green-500">{message}</p>
        )}

        <form onSubmit={handleUpdate}>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">New Username</label>
            <input
              type="text"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
              required
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">Upload Avatar</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleAvatarChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
          >
            Update Username and Avatar
          </button>
        </form>
      </div>
    </div>
  );
};

export default AccountSettings;
