import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <div className="min-h-screen bg-primary">
      {/* Верхняя панель */}
      <nav className="bg-[#ae4a7e] p-2 shadow-lg fixed w-full top-0 z-10">
        <div className="container mx-auto flex justify-between items-center">
          {/* Левая часть панели */}
          <div className="text-white text-lg font-semibold">
            {isAuthenticated && (
              <span>Welcome, {username}!</span>
            )}
          </div>

          {/* Правая часть панели */}
          <div className="flex items-center space-x-4">
            {isAuthenticated ? (
              <>
                <Link to={`/users/${username}`} className="text-white hover:text-gray-200 transition-colors">
                  My Profile
                </Link>
                <Link to="/account-settings" className="text-white hover:text-gray-200 transition-colors">
                  Account Settings
                </Link>
                <button
                  onClick={handleLogout}
                  className="text-white hover:text-gray-200 transition-colors"
                >
                  Log Out
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="text-white hover:text-gray-200 transition-colors">
                  Login
                </Link>
                <Link to="/register" className="text-white hover:text-gray-200 transition-colors">
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </nav>

      {/* Контент */}
      <div className="pt-16">
        <div className="container mx-auto">
          {/* Основной контент здесь */}
          <h1 className="text-center text-3xl font-bold text-white mt-10">Welcome to the Home Page!</h1>
        </div>
      </div>
    </div>
  );
};

export default Home;
