import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  let dropdownTimer;

  const handleMouseEnter = () => {
    clearTimeout(dropdownTimer);
    setIsDropdownOpen(true);
  };

  const handleMouseLeave = () => {
    dropdownTimer = setTimeout(() => {
      setIsDropdownOpen(false);
    }, 300);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleCreateArt = () => {
    // Логика открытия модуля Create Art
    closeModal();
    window.location.href = '/create-art'; // Или можно использовать React Router для навигации
  };

  const handleCreatePalette = () => {
    closeModal();
    window.location.href = '/create-palette'; // Или можно использовать React Router для навигации
  };

  return (
    <div className="min-h-screen bg-primary">
      {/* Верхняя панель */}
      <nav className="bg-[#031634] p-2 shadow-lg fixed w-full top-0 z-10">
        <div className="container mx-auto flex justify-between items-center">
          {/* Левая часть панели с логотипом */}
          <div className="flex items-center space-x-4">
            <Link to="/">
              <img src={logo} alt="Logo" className="h-16" />
            </Link>
            <Link to="/">
              <span className="text-white text-2xl font-semibold font-sans">Tintify</span>
            </Link>
          </div>

          {/* Правая часть панели */}
          {isAuthenticated ? (
            <div className="relative" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
              <text className="text-center text-white cursor-pointer">Welcome, {username}!</text>
              {isDropdownOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white shadow-md rounded-md z-20">
                  <Link to={`/users/${username}`} className="block px-4 py-2 text-black hover:bg-gray-200">
                    My Profile
                  </Link>
                  <Link to="/account-settings" className="block px-4 py-2 text-black hover:bg-gray-200">
                    Account Settings
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left px-4 py-2 text-black hover:bg-gray-200"
                  >
                    Log Out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="flex items-center space-x-4">
              <Link to="/login" className="text-white hover:text-gray-200 transition-colors">
                Login
              </Link>
              <Link to="/register" className="text-white hover:text-gray-200 transition-colors">
                Register
              </Link>
            </div>
          )}

          {/* Кнопка "Create" */}
          <button
            onClick={openModal}
            className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all ml-4"
          >
            Create
          </button>
        </div>
      </nav>

      {/* Модальное окно */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-2xl font-bold mb-4">Choose an option</h2>
            <button
              onClick={handleCreateArt}
              className="bg-primary text-white py-2 px-4 rounded mb-4 hover:bg-secondary transition-all w-full"
            >
              Create Art
            </button>
            <button
              onClick={handleCreatePalette}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all w-full"
            >
              Create Palette
            </button>
            <button
              onClick={closeModal}
              className="mt-4 text-gray-500 hover:text-black"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Контент */}
      <div className="pt-16">
        <div className="container mx-auto">
          <h1 className="text-center text-3xl font-bold text-white mt-10">Welcome to the Home Page!</h1>
        </div>
      </div>
    </div>
  );
};

export default Home;
