import React, { useState, useRef, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import axios from 'axios';
import Navbar from './common/Navbar'; // Верхняя панель

const CreateArt = () => {
  const [width, setWidth] = useState(10); // Ширина матрицы пиксельного рисунка
  const [height, setHeight] = useState(10); // Высота матрицы пиксельного рисунка
  const [pixelSize, setPixelSize] = useState(46); // Размер каждого пикселя
  const [pixels, setPixels] = useState(Array(width * height).fill('#ffffff')); // Изначальные цвета пикселей

  const [palette, setPalette] = useState(Array(3 * 3).fill('#ffffff')); // Изначальная палитра 3x3
  const [paletteWidth] = useState(3); // Фиксированная ширина палитры
  const [paletteHeight, setPaletteHeight] = useState(3); // Изначальная высота палитры
  const [maxPaletteHeight] = useState(10); // Максимальная высота палитры

  const [currentColor, setCurrentColor] = useState('#000000'); // Текущий цвет
  const [pickerVisible, setPickerVisible] = useState(false); // Видимость цветового круга
  const [selectedPixel, setSelectedPixel] = useState(null); // Выбранный пиксель или цвет из палитры
  const [activePaletteIndex, setActivePaletteIndex] = useState(null); // Индекс активного цвета палитры
  const [position, setPosition] = useState({ top: 0, left: 0 }); // Позиция цветового круга

  const [isDrawing, setIsDrawing] = useState(false); // Состояние рисования

  const [isModalOpen, setIsModalOpen] = useState(false); // Видимость модального окна
  const [userPalettes, setUserPalettes] = useState([]); // Палитры пользователя
  const [likedPalettes, setLikedPalettes] = useState([]); // Лайкнутые палитры
  const [allPalettes, setAllPalettes] = useState([]);
  const [activeTab, setActiveTab] = useState('my-palettes');
  const [searchQuery, setSearchQuery] = useState('');

  const [artName, setArtName] = useState(''); // Название арта
  const [isFinal, setIsFinal] = useState(false); // Состояние финальной версии арта
  const [isPublic, setIsPublic] = useState(false); // Состояние публичного арта
  const [isSaveModalOpen, setIsSaveModalOpen] = useState(false); // Видимость модального окна сохранения
  const [isCheckboxDisabled, setIsCheckboxDisabled] = useState(false); // Отключение чекбокса

  const pickerRef = useRef(null);
  const modalRef = useRef(null);
  const saveModalRef = useRef(null);

  const isAuthenticated = sessionStorage.getItem('authToken') ? true : false;

  useEffect(() => {
    if (!isAuthenticated) {
      setIsPublic(true);  // Устанавливаем арт публичным для неаутентифицированных пользователей
      setIsCheckboxDisabled(true);  // Делаем чекбокс неактивным
    }
  }, [isAuthenticated]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        setPickerVisible(false);
      }
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setIsModalOpen(false); // Закрыть модальное окно палитры
      }
      if (saveModalRef.current && !saveModalRef.current.contains(event.target)) {
        setIsSaveModalOpen(false); // Закрыть модальное окно сохранения
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleSaveArtAsDraft = () => {
    const artData = {
      name: artName,
      isFinal: false,
      isPublic,
      pixels, // Массив с цветами пикселей
      width,
      height,
    };

    axios
      .post(`http://localhost:8080/art/create`, artData, { withCredentials: true })
      .then((response) => {
        console.log('Art saved successfully', response.data);
        setIsSaveModalOpen(false); // Закрываем окно после успешного сохранения
      })
      .catch((error) => console.error('Error saving art:', error));
  };

  const handleSaveArtAsFinal = () => {
    const artData = {
      name: artName,
      isFinal: true,
      isPublic,
      pixels, // Массив с цветами пикселей
      width,
      height,
    };

    axios
      .post(`http://localhost:8080/art/create`, artData, { withCredentials: true })
      .then((response) => {
        console.log('Art saved successfully', response.data);
        setIsSaveModalOpen(false); // Закрываем окно после успешного сохранения
      })
      .catch((error) => console.error('Error saving art:', error));
  };

  const handleSaveClick = () => {
    setIsSaveModalOpen(true);
  };

  const closeModal = () => {
    setIsSaveModalOpen(false);
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar showSaveButton={true} onSaveClick={handleSaveClick} />

      {/* Содержимое страницы Pixel Art */}

      {/* Всплывающее окно сохранения */}
      {isSaveModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div ref={saveModalRef} className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold mb-4">Save art</h2>

            <div className="mb-4">
              <label className="block mb-2">Art name:</label>
              <input
                type="text"
                value={artName}
                onChange={(e) => setArtName(e.target.value)}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                required
              />
            </div>

            <div className="mb-4">
              <label className="inline-flex items-center">
                <input
                  type="checkbox"
                  checked={isPublic}
                  onChange={() => setIsPublic(!isPublic)}
                  className="mr-2"
                  disabled={isCheckboxDisabled}
                />
                Make this art public
              </label>
            </div>

            <div className="flex justify-end space-x-4">
              <button onClick={closeModal} className="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500">
                Cancel
              </button>
              <button
                onClick={handleSaveArtAsFinal}
                className="px-4 py-2 bg-primary text-white rounded hover:bg-secondary"
              >
                Save art as final
              </button>
              <button
                onClick={handleSaveArtAsDraft}
                className="px-4 py-2 bg-primary text-white rounded hover:bg-secondary"
              >
                Save art as draft
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreateArt;
