import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../../assets/logo.jpg';

const Navbar = ({ showSaveButton, onSaveClick }) => {
  const isAuthenticated = sessionStorage.getItem('authToken');
  const username = sessionStorage.getItem('username');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Для управления состоянием выпадающего меню
  const location = useLocation(); // Для получения текущего пути

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  // Функции для открытия/закрытия dropdown при наведении
  const handleMouseEnter = () => setIsDropdownOpen(true);
  const handleMouseLeave = () => setIsDropdownOpen(false);

  return (
    <nav className="bg-[#031634] p-4 shadow-md fixed w-full top-0 z-10">
      <div className="container mx-auto flex justify-between items-center">
        {/* Левая часть панели с логотипом и названием */}
        <div className="flex items-center space-x-4">
          <Link to="/">
            <img src={logo} alt="Logo" className="h-24" />
          </Link>
          <Link to="/">
            <span className="text-white text-2xl font-semibold">Tintify</span>
          </Link>
        </div>

        {/* Правая часть панели */}
        <div className="flex items-center space-x-4">
          {/* Показывать кнопку Create только на страницах, где она нужна */}
          {(location.pathname === '/home' || location.pathname === '/') && (
            <button
              onClick={() => alert('Open Create Modal')}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
            >
              Create
            </button>
          )}

          {showSaveButton && (
            <button
              onClick={onSaveClick}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
            >
              Save
            </button>
          )}

          {isAuthenticated ? (
            <div
              className="relative"
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              {/* Надпись "Welcome" */}
              <span className="text-white cursor-pointer">Welcome, {username}!</span>

              {/* Выпадающее меню */}
              {isDropdownOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white shadow-md rounded-md z-20">
                  <Link
                    to={`/users/${username}`}
                    className="block px-4 py-2 text-black hover:bg-gray-200"
                  >
                    My Profile
                  </Link>
                  <Link
                    to="/account-settings"
                    className="block px-4 py-2 text-black hover:bg-gray-200"
                  >
                    Account Settings
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left px-4 py-2 text-black hover:bg-gray-200"
                  >
                    Log Out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <>
              <Link
                to="/login"
                className="text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
