import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Navbar = ({ isAuthenticated, username, onLogout, isCreatePage }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleMouseEnter = () => {
    setIsDropdownOpen(true);
  };

  const handleMouseLeave = () => {
    setIsDropdownOpen(false);
  };

  return (
    <nav className="bg-[#ae4a7e] p-4 shadow-md fixed w-full top-0 z-10">
      <div className="container mx-auto flex justify-between items-center">
        {/* Левая часть панели с логотипом */}
        <div className="flex items-center space-x-4">
          <Link to="/">
            <img src={logo} alt="Logo" className="h-10" />
          </Link>
          <Link to="/">
            <span className="text-white text-2xl font-semibold">Tintify</span>
          </Link>
        </div>

        {/* Правая часть панели */}
        <div className="flex items-center space-x-4">
          {isAuthenticated ? (
            <div
              className="relative"
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <span className="text-white cursor-pointer">Welcome, {username}!</span>
              {isDropdownOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white shadow-md rounded-md z-20">
                  <Link to={`/users/${username}`} className="block px-4 py-2 text-black hover:bg-gray-200">
                    My Profile
                  </Link>
                  <Link to="/account-settings" className="block px-4 py-2 text-black hover:bg-gray-200">
                    Account Settings
                  </Link>
                  <button
                    onClick={onLogout}
                    className="block w-full text-left px-4 py-2 text-black hover:bg-gray-200"
                  >
                    Log Out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <>
              <Link to="/login" className="text-white hover:text-gray-200 transition-colors">
                Login
              </Link>
              <Link to="/register" className="text-white hover:text-gray-200 transition-colors">
                Register
              </Link>
            </>
          )}

          {/* Кнопка "Create" только на странице создания палитр или артов */}
          {isCreatePage && (
            <button className="bg-white text-[#ae4a7e] py-2 px-4 rounded hover:bg-gray-100">
              Save
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
