import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../../assets/logo.jpg';

const Navbar = ({ isAuthenticated, username, isCreatePalettePage }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <nav className="bg-[#ae4a7e] p-4 shadow-md fixed w-full top-0 z-10">
      <div className="container mx-auto flex justify-between items-center">
        {/* Левая часть панели */}
        <div className="flex items-center space-x-4">
          <Link to="/">
            <img src={logo} alt="Logo" className="h-10" />
          </Link>
          <Link to="/">
            <span className="text-white text-2xl font-semibold">Tintify</span>
          </Link>
        </div>

        {/* Правая часть панели */}
        <div className="relative">
          {isAuthenticated && (
            <>
              <button
                onMouseEnter={toggleDropdown} // Открытие меню при наведении
                onMouseLeave={toggleDropdown} // Закрытие меню при отводе мыши
                className="text-white hover:text-gray-200 transition-colors"
              >
                Welcome, {username}!
              </button>

              {/* Выпадающее меню */}
              {isDropdownOpen && (
                <div
                  className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg"
                  onMouseEnter={toggleDropdown}
                  onMouseLeave={toggleDropdown}
                >
                  <Link
                    to={`/users/${username}`}
                    className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                  >
                    My Profile
                  </Link>
                  <Link
                    to="/account-settings"
                    className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                  >
                    Account Settings
                  </Link>
                  <button
                    className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200"
                    onClick={() => {
                      sessionStorage.removeItem('authToken');
                      sessionStorage.removeItem('username');
                      window.location.href = '/#/login';
                    }}
                  >
                    Log Out
                  </button>
                </div>
              )}
            </>
          )}

          {!isAuthenticated && (
            <div className="space-x-4">
              <Link to="/login" className="text-white hover:text-gray-200 transition-colors">
                Login
              </Link>
              <Link to="/register" className="text-white hover:text-gray-200 transition-colors">
                Register
              </Link>
            </div>
          )}

          {/* Кнопка "Save" на странице create-palette */}
          {isCreatePalettePage && (
            <button className="ml-4 bg-white text-[#ae4a7e] py-2 px-4 rounded hover:bg-gray-100">
              Save
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
