import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <div className="min-h-screen bg-[#011d42] text-white">
      <nav className="bg-[#ae4a7e] p-4 shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          {/* Логотип и название слева */}
          <div className="flex items-center space-x-4">
            <img src="/assets/logo.jpg" alt="Logo" className="h-8 w-auto" />
            <Link to="/" className="text-white text-xl font-bold">Tintify</Link>
          </div>

          {/* Кнопки справа с выпадающим меню */}
          <div
            className="relative"
            onMouseEnter={() => setMenuOpen(true)}
            onMouseLeave={() => setMenuOpen(false)}
          >
            <button className="text-white text-lg focus:outline-none">
              {isAuthenticated ? `Welcome, ${username}` : 'Menu'}
            </button>
            {menuOpen && (
              <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-lg py-2">
                {isAuthenticated ? (
                  <div className="flex flex-col">
                    <Link
                      to={`/users/${username}`}
                      className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                    >
                      My Profile
                    </Link>
                    <Link
                      to="/account-settings"
                      className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                    >
                      Account Settings
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                    >
                      Log Out
                    </button>
                  </div>
                ) : (
                  <div className="flex flex-col">
                    <Link
                      to="/login"
                      className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                    >
                      Login
                    </Link>
                    <Link
                      to="/register"
                      className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                    >
                      Register
                    </Link>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </nav>

      <div className="container mx-auto mt-10 text-center">
        <h1 className="text-4xl font-bold mb-4">Welcome to the Home Page!</h1>
        <p className="text-lg">You have successfully logged in.</p>
      </div>
    </div>
  );
};

export default Home;
