const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <div className="min-h-screen bg-primary">
      <nav className="bg-primary p-4 shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex space-x-4">
            {isAuthenticated ? (
              <div className="text-center">
                <p className="text-lg mb-4 text-white">
                  Logged in as: <span className="font-semibold">{username}</span>
                </p>
                <div className="mb-4">
                  <Link
                    to={`/users/${username}`}
                    className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
                  >
                    My Profile
                  </Link>
                  <Link
                    to="/account-settings"
                    className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
                  >
                    Account Settings
                  </Link>
                </div>
                <button
                  onClick={handleLogout}
                  className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
                >
                  Log Out
                </button>
              </div>
            ) : (
              <div className="text-center">
                <p className="text-lg mb-4 text-white">You are not logged in.</p>
                <Link to="/login">
                  <button className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all mr-4">
                    Login
                  </button>
                </Link>
                <Link to="/register">
                  <button className="bg-secondary text-white py-2 px-4 rounded hover:bg-primary transition-all">
                    Register
                  </button>
                </Link>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Home;
