import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import Navbar from './common/Navbar';
import likeIcon from '../assets/like-icon.png';  // Иконка "лайк"
import likedIcon from '../assets/liked-icon.png'; // Иконка "лайкнутого"

const UserProfile = () => {
  const { username } = useParams();
  const [user, setUser] = useState(null);
  const [palettes, setPalettes] = useState([]);
  const [likedPalettes, setLikedPalettes] = useState([]);
  const [arts, setArts] = useState([]);
  const [avatar, setAvatar] = useState(null);
  const [currentUser, setCurrentUser] = useState('');
  const [activeTab, setActiveTab] = useState('palettes');
  const [likesModalOpen, setLikesModalOpen] = useState(false);
  const [likesUsers, setLikesUsers] = useState([]); // Для хранения пользователей, лайкнувших палитру
  const [selectedPalette, setSelectedPalette] = useState(null); // Палитра, для которой открываем список лайкнувших

  const modalRef = useRef(null);

  useEffect(() => {
    const loggedInUser = sessionStorage.getItem('username');
    setCurrentUser(loggedInUser || '');

    // Загружаем данные пользователя
    axios.get(`http://localhost:8080/users/${username}`)
      .then(response => {
        setUser(response.data);
        setAvatar(`http://localhost:8080${response.data.avatarPath}`);
      })
      .catch(error => console.error('Error fetching user data:', error));

    // Загружаем публичные палитры
    axios.get(`http://localhost:8080/palettes/user/${username}/public`)
      .then(response => setPalettes(response.data))
      .catch(error => console.error('Error fetching public palettes:', error));

    // Загружаем палитры, лайкнутые залогиненным пользователем
    if (loggedInUser) {
      axios.get(`http://localhost:8080/palettes/user/${loggedInUser}/likes`, { withCredentials: true })
        .then(response => setLikedPalettes(response.data))
        .catch(error => console.error('Error fetching liked palettes:', error));
    }

    // Загружаем публичные арты
    axios.get(`http://localhost:8080/arts/user/${username}/public`)
      .then(response => setArts(response.data))
      .catch(error => console.error('Error fetching public arts:', error));
  }, [username]);

  // Открытие модального окна для отображения пользователей, лайкнувших палитру
  const handleShowLikes = (paletteId) => {
    axios.get(`http://localhost:8080/palettes/${paletteId}/likes/users`)
      .then(response => {
        setLikesUsers(response.data); // Сохраняем пользователей, лайкнувших палитру
        setSelectedPalette(paletteId);
        setLikesModalOpen(true); // Открываем модальное окно
      })
      .catch(error => console.error('Error fetching users who liked the palette:', error));
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setLikesModalOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLike = (paletteId) => {
    axios.post(`http://localhost:8080/palettes/${paletteId}/like`, {}, {
      withCredentials: true
    })
    .then(() => {
      // Обновляем лайки
      setPalettes(palettes.map(palette =>
        palette.id === paletteId ? { ...palette, likes: palette.likes + 1 } : palette
      ));

      // Обновляем лайкнутые палитры
      if (likedPalettes.some(palette => palette.id === paletteId)) {
        setLikedPalettes(likedPalettes.filter(palette => palette.id !== paletteId));
      } else {
        const likedPalette = palettes.find(palette => palette.id === paletteId);
        setLikedPalettes([...likedPalettes, likedPalette]);
      }
    })
    .catch((error) => console.error('Error liking palette:', error));
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar />
      <div className="container mx-auto py-8 text-center">
        <img src={avatar} alt="User Avatar" className="rounded-full h-32 w-32 object-cover mx-auto mb-4" />
        <h1 className="text-3xl font-bold text-white">{user?.username}</h1>

        {/* Вкладки */}
        <div className="mt-8 flex justify-center space-x-8">
          <button
            onClick={() => setActiveTab('palettes')}
            className={`px-4 py-2 ${activeTab === 'palettes' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
          >
            Palettes
          </button>
          <button
            onClick={() => setActiveTab('liked-palettes')}
            className={`px-4 py-2 ${activeTab === 'liked-palettes' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
          >
            Liked Palettes
          </button>
          <button
            onClick={() => setActiveTab('arts')}
            className={`px-4 py-2 ${activeTab === 'arts' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
          >
            Arts
          </button>
        </div>

        {/* Содержимое вкладок */}
        <div className="mt-8">
          {activeTab === 'palettes' && (
            <>
              <h2 className="text-2xl font-semibold text-white mb-4">Palettes</h2>
              {palettes.length > 0 ? (
                <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {palettes.map((palette, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                      <h3 className="text-lg font-bold mb-2 text-white">{palette.name}</h3>
                      <p className="text-sm text-white mb-4">by <Link to={`/users/${palette.user.username}`} className="underline">{palette.user.username}</Link></p>
                      <div className="grid grid-cols-4" style={{ gap: '0px' }}>
                        {palette.colors.map((color, idx) => (
                          <div
                            key={idx}
                            className="w-8 h-8 md:w-10 md:h-10 lg:w-12 lg:h-12"
                            style={{ backgroundColor: color }}
                          />
                        ))}
                      </div>
                      <div className="flex items-center justify-between mt-4">
                        <button
                          onClick={() => handleLike(palette.id)}
                          disabled={currentUser === username}
                          className={`flex items-center ${currentUser === username ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105 transition-transform'}`}
                        >
                          <img
                            src={likedPalettes.some(p => p.id === palette.id) ? likedIcon : likeIcon}
                            alt="Like"
                            className="h-6 w-6 mr-2"
                          />
                          <span className="text-white text-xs">{palette.likes} Likes</span>
                        </button>
                        {currentUser === username && (
                          <button
                            onClick={() => handleShowLikes(palette.id)}
                            className="text-white text-xs underline"
                          >
                            Show likes
                          </button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-white">This user has no public palettes yet.</p>
              )}
            </>
          )}

          {/* Лайкнутые палитры */}
          {activeTab === 'liked-palettes' && (
            <>
              <h2 className="text-2xl font-semibold text-white mb-4">Liked Palettes</h2>
              {likedPalettes.length > 0 ? (
                <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {likedPalettes.map((palette, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                      <h3 className="text-lg font-bold mb-2 text-white">{palette.name}</h3>
                      <p className="text-sm text-white mb-4">by <Link to={`/users/${palette.user.username}`} className="underline">{palette.user.username}</Link></p>
                      <div className="grid grid-cols-4" style={{ gap: '0px' }}>
                        {palette.colors.map((color, idx) => (
                          <div
                            key={idx}
                            className="w-8 h-8 md:w-10 md:h-10 lg:w-12 lg:h-12"
                            style={{ backgroundColor: color }}
                          />
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-white">This user has not liked any public palettes yet.</p>
              )}
            </>
          )}

          {/* Арты пользователя */}
          {activeTab === 'arts' && (
            <>
              <h2 className="text-2xl font-semibold text-white mb-4">Arts</h2>
              {arts.length > 0 ? (
                <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                  {arts.map((art, index) => (
                    <div key={index} className="flex flex-col items-center">
                      <h3 className="text-lg font-bold mb-2 text-white">{art.name}</h3>
                      <div
                        className="grid"
                        style={{
                          gridTemplateColumns: `repeat(${art.width}, 1fr)`,
                          gap: '0px',
                          width: `${art.width * 20}px`,
                          height: `${art.height * 20}px`,
                        }}
                      >
                        {art.pixels.map((color, idx) => (
                          <div
                            key={idx}
                            style={{
                              backgroundColor: color,
                              width: '100%',
                              height: '100%',
                            }}
                          />
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-white">This user has no public arts yet.</p>
              )}
            </>
          )}

          {/* Модальное окно с пользователями, лайкнувшими палитру */}
          {likesModalOpen && (
            <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
              <div ref={modalRef} className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full">
                <h2 className="text-2xl font-bold mb-4">Users who liked this palette</h2>
                <button onClick={() => setLikesModalOpen(false)} className="absolute top-2 right-2 text-gray-500 hover:text-black">
                  Close
                </button>
                <div className="grid grid-cols-3 gap-4">
                  {likesUsers && Array.isArray(likesUsers) && likesUsers.length > 0 ? (
                    likesUsers.map((user, index) => (
                      <div key={index} className="flex items-center space-x-2">
                        <img
                          src={`http://localhost:8080${user.avatarPath}`}
                          alt={`${user.username}'s avatar`}
                          className="w-10 h-10 rounded-full"
                        />
                        <p className="text-black">{user.username}</p>
                      </div>
                    ))
                  ) : (
                    <p className="text-black">No likes yet.</p>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
