import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Navbar from './common/Navbar';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isPublic, setIsPublic] = useState(true); // Устанавливаем по умолчанию в true

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    } else {
      setIsPublic(true); // Если не аутентифицирован, палитра будет публичной
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleCreateArt = () => {
    closeModal();
    window.location.href = '/create-art';
  };

  const handleCreatePalette = () => {
    closeModal();
    window.location.href = '/create-palette';
  };

  return (
    <div className="min-h-screen bg-primary">
      {/* Верхняя панель */}
      <Navbar
        username={username}
        isAuthenticated={isAuthenticated}
        handleLogout={handleLogout}
        onHomePage={true}  // Указываем, что мы на главной странице
        openModal={openModal}  // Передаем функцию для открытия модального окна
      />

      {/* Модальное окно */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-2xl font-bold mb-4">Create New Palette</h2>

            {/* Поле для названия палитры */}
            <input
              type="text"
              placeholder="Enter palette name"
              className="border rounded px-4 py-2 mb-4 w-full"
            />

            {/* Переключатель для публичности */}
            <div className="mb-4">
              <label className="inline-flex items-center">
                <input
                  type="checkbox"
                  checked={isPublic} // Автоматически установлено в true
                  disabled={!isAuthenticated} // Отключаем, если пользователь не авторизован
                  onChange={(e) => setIsPublic(e.target.checked)}
                  className="form-checkbox"
                />
                <span className="ml-2">Make this palette public</span>
              </label>
            </div>

            {/* Кнопки для создания арта и палитры */}
            <button
              onClick={handleCreateArt}
              className="bg-primary text-white py-2 px-4 rounded mb-4 hover:bg-secondary transition-all w-full"
            >
              Create Art
            </button>
            <button
              onClick={handleCreatePalette}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all w-full"
            >
              Create Palette
            </button>
            <button
              onClick={closeModal}
              className="mt-4 text-gray-500 hover:text-black"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Контент */}
      <div className="pt-16">
        <div className="container mx-auto">
          <h1 className="text-center text-3xl font-bold text-white mt-10">Welcome to the Home Page!</h1>
        </div>
      </div>
    </div>
  );
};

export default Home;
