import React, { useState, useRef, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import axios from 'axios';
import Navbar from './common/Navbar'; // Верхняя панель

const CreateArt = () => {
  const [width, setWidth] = useState(10); // Ширина матрицы пиксельного рисунка
  const [height, setHeight] = useState(10); // Высота матрицы пиксельного рисунка
  const [pixelSize, setPixelSize] = useState(46); // Размер каждого пикселя
  const [pixels, setPixels] = useState(Array(width * height).fill('#ffffff')); // Изначальные цвета пикселей

  const [palette, setPalette] = useState(Array(3 * 3).fill('#ffffff')); // Изначальная палитра 3x3
  const [paletteWidth] = useState(3); // Фиксированная ширина палитры
  const [paletteHeight, setPaletteHeight] = useState(3); // Изначальная высота палитры
  const [maxPaletteHeight] = useState(10); // Максимальная высота палитры

  const [currentColor, setCurrentColor] = useState('#000000'); // Текущий цвет
  const [pickerVisible, setPickerVisible] = useState(false); // Видимость цветового круга
  const [selectedPixel, setSelectedPixel] = useState(null); // Выбранный пиксель или цвет из палитры
  const [activePaletteIndex, setActivePaletteIndex] = useState(null); // Индекс активного цвета палитры
  const [position, setPosition] = useState({ top: 0, left: 0 }); // Позиция цветового круга

  const [isDrawing, setIsDrawing] = useState(false); // Состояние рисования

  const [isModalOpen, setIsModalOpen] = useState(false); // Видимость модального окна
  const [userPalettes, setUserPalettes] = useState([]); // Палитры пользователя

  const pickerRef = useRef(null);
  const modalRef = useRef(null); // Для отслеживания кликов вне модального окна

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        setPickerVisible(false);
      }

      // Закрытие модального окна при клике вне его
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setIsModalOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Изменение цвета выбранного квадрата
  const handleColorChange = (color) => {
    setCurrentColor(color.hex); // Обновляем текущий цвет
    if (selectedPixel !== null) {
      if (selectedPixel < palette.length) {
        // Если был выбран квадрат палитры
        const newPalette = [...palette];
        newPalette[selectedPixel] = color.hex;
        setPalette(newPalette); // Обновляем палитру
      } else {
        const newPixels = [...pixels];
        newPixels[selectedPixel - palette.length] = color.hex;
        setPixels(newPixels); // Обновляем пиксели
      }
    }
  };

  // Обработка клика на пиксель или цвет из палитры
  const handleSquareClick = (index, event, isPalette) => {
    if (isPalette) {
      // Если выбран квадрат палитры
      if (activePaletteIndex === index) {
        // Если клик повторный, "откликиваем" цвет
        setActivePaletteIndex(null);
        setCurrentColor('#000000'); // Сбрасываем текущий цвет
      } else {
        // Иначе делаем квадрат активным
        setSelectedPixel(index); // Запоминаем выбранный индекс палитры
        setActivePaletteIndex(index); // Запоминаем активный квадрат палитры
        setCurrentColor(palette[index]); // Устанавливаем текущий цвет
      }

      setPickerVisible(true); // Показываем цветовое кольцо

      const rect = event.target.getBoundingClientRect();
      setPosition({
        top: rect.top + window.scrollY + rect.height, // Появляется ниже квадрата
        left: rect.left + window.scrollX, // Появляется на месте клика
      });
    } else {
      // Если выбран пиксель на арте
      if (activePaletteIndex === null) {
        // Если не выбран активный цвет из палитры, показываем цветовой круг
        setSelectedPixel(index + palette.length);
        setPickerVisible(true);

        const rect = event.target.getBoundingClientRect();
        setPosition({
          top: rect.top + window.scrollY + rect.height, // Появляется ниже пикселя
          left: rect.left + window.scrollX, // Появляется на месте клика
        });
      } else {
        // Если цвет палитры активен, закрашиваем пиксель этим цветом
        const newPixels = [...pixels];
        newPixels[index] = currentColor; // Закрашиваем пиксель текущим цветом
        setPixels(newPixels);
      }
    }
  };

  const handleMouseDown = (index, isPalette) => {
    if (!isPalette && activePaletteIndex !== null) {
      const newPixels = [...pixels];
      newPixels[index] = currentColor;
      setPixels(newPixels);
      setIsDrawing(true);
    }
  };

  const handleMouseMove = (index) => {
    if (isDrawing) {
      const newPixels = [...pixels];
      newPixels[index] = currentColor;
      setPixels(newPixels);
    }
  };

  const handleMouseUp = () => {
    setIsDrawing(false);
  };

  const handleWidthChange = (e) => {
    const newWidth = Math.max(2, Math.min(32, Number(e.target.value)));
    setWidth(newWidth);
    setPixels(Array(newWidth * height).fill('#ffffff'));
  };

  const handleHeightChange = (e) => {
    const newHeight = Math.max(2, Math.min(32, Number(e.target.value)));
    setHeight(newHeight);
    setPixels(Array(width * newHeight).fill('#ffffff'));
  };

  // Увеличение палитры (добавление одного квадрата)
  const addPaletteSquare = () => {
    const currentSquares = palette.length;
    const maxSquares = paletteWidth * maxPaletteHeight;

    if (currentSquares < maxSquares) {
      // Если палитра еще не достигла максимальной высоты, добавляем вертикально
      setPalette([...palette, '#ffffff']);
    } else {
      // Если достигнута максимальная высота, увеличиваем ширину палитры
      setPaletteHeight(paletteHeight + 1);
      setPalette([...palette, '#ffffff']);
    }
  };

  // Уменьшение палитры (удаление последнего квадрата)
  const removePaletteSquare = () => {
    if (palette.length > 0) {
      setPalette(palette.slice(0, palette.length - 1));
    }
  };

  const handlePixelSizeChange = (e) => {
    const newSize = Math.max(4, Math.min(64, Number(e.target.value))); // Ограничиваем размер от 4 до 64
    setPixelSize(newSize);
  };

  // Открытие модального окна и загрузка палитр пользователя
  const openModal = () => {
    const username = sessionStorage.getItem('username');
    axios
      .get(`http://localhost:8080/palettes/user/${username}`, { withCredentials: true })
      .then((response) => {
        setUserPalettes(response.data);
        setIsModalOpen(true);
      })
      .catch((error) => console.error('Error fetching user palettes:', error));
  };

  // Загрузка выбранной палитры в панель Palette
  const handlePaletteSelect = (palette) => {
    setPalette(palette.colors);
    setPaletteHeight(Math.ceil(palette.colors.length / 3)); // Устанавливаем высоту палитры
    setIsModalOpen(false); // Закрываем модальное окно после выбора палитры
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar />
      <div className="container mx-auto py-8 flex justify-between">
        {/* Палитра слева */}
        <div className="flex flex-col items-start" style={{ marginLeft: '20px' }}>
          <h2 className="text-white text-xl mb-4">Palette</h2>
          <div className="flex mb-4 space-x-2">
            <button
              onClick={addPaletteSquare}
              className="bg-secondary text-white w-10 h-10 rounded hover:bg-secondary-dark"
            >
              +
            </button>
            <button
              onClick={removePaletteSquare}
              className="bg-secondary text-white w-10 h-10 rounded hover:bg-secondary-dark"
            >
              -
            </button>
            <button
              onClick={openModal}
              className="bg-secondary text-white w-10 h-10 rounded hover:bg-secondary-dark"
            >
              ...
            </button>
          </div>
          <div className="grid" style={{ gridTemplateColumns: `repeat(${paletteWidth}, 1fr)`, gap: '0px' }}>
            {palette.map((color, index) => (
              <div
                key={index}
                className={`w-[46px] h-[46px] cursor-pointer border ${
                  activePaletteIndex === index ? 'border-4 border-yellow-500' : ''
                }`}
                style={{ backgroundColor: color }}
                onClick={(e) => handleSquareClick(index, e, true)}
              />
            ))}
          </div>
        </div>

        {/* Матрица пикселей */}
        <div className="flex flex-col items-center mx-auto">
          <h2 className="text-white text-xl mb-4">Pixel Art</h2>
          {/* Настройки размеров */}
          <div className="flex justify-center mb-8 space-x-4">
            <div>
              <label className="text-white mr-2">Width:</label>
              <input
                type="number"
                value={width}
                onChange={handleWidthChange}
                min="2"
                max="32"
                className="px-2 py-1 rounded text-black"
              />
            </div>

            <div>
              <label className="text-white mr-2">Height:</label>
              <input
                type="number"
                value={height}
                onChange={handleHeightChange}
                min="2"
                max="32"
                className="px-2 py-1 rounded text-black"
              />
            </div>

            <div>
              <label className="text-white mr-2">Pixel Size:</label>
              <input
                type="number"
                value={pixelSize}
                onChange={handlePixelSizeChange}
                min="4"
                max="64"
                className="px-2 py-1 rounded text-black"
              />
            </div>
          </div>

          <div
            className="grid"
            style={{ gridTemplateColumns: `repeat(${width}, 1fr)`, gap: '0px' }}
            onMouseUp={handleMouseUp}
          >
            {pixels.map((color, index) => (
              <div
                key={index}
                className="cursor-pointer border"
                style={{
                  backgroundColor: color,
                  width: `${pixelSize}px`,
                  height: `${pixelSize}px`,
                }}
                onMouseDown={() => handleMouseDown(index, false)}
                onMouseMove={() => handleMouseMove(index)}
                onClick={(e) => handleSquareClick(index, e, false)}
              />
            ))}
          </div>
        </div>
      </div>

      {/* Цветовое колесо */}
      {pickerVisible && (
        <div
          ref={pickerRef}
          style={{
            position: 'absolute',
            top: `${position.top}px`,
            left: `${position.left}px`,
            zIndex: 1000,
          }}
        >
          <ChromePicker color={currentColor} onChangeComplete={handleColorChange} />
        </div>
      )}

      {/* Модальное окно с палитрами пользователя */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div ref={modalRef} className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full">
            <h2 className="text-2xl font-bold mb-4">Your Palettes</h2>
            <button onClick={() => setIsModalOpen(false)} className="absolute top-2 right-2 text-gray-500 hover:text-black">
              Close
            </button>
            <div className="grid grid-cols-4 gap-4">
              {userPalettes.map((palette, index) => (
                <div key={index} className="p-4 border rounded-lg cursor-pointer" onClick={() => handlePaletteSelect(palette)}>
                  <h3 className="font-bold mb-2">{palette.name}</h3>
                  <div className="grid grid-cols-4 gap-1">
                    {palette.colors.map((color, colorIndex) => (
                      <div
                        key={colorIndex}
                        className="w-8 h-8 border"
                        style={{ backgroundColor: color }}
                      />
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreateArt;
