import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [username, setUsername] = useState('');

  useEffect(() => {
    const savedUsername = sessionStorage.getItem('username');
    if (savedUsername) {
      setUsername(savedUsername);
    }
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-pixelBg">
      <div className="bg-white shadow-lg rounded-lg p-8 max-w-md w-full text-center">
        <h1 className="text-3xl font-bold text-primary mb-6">Welcome to the Home Page!</h1>

        {username && (
          <>
            <p className="mb-4">Logged in as: {username}</p>
            <div className="space-y-4">
              <Link to={`/users/${username}`} className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                My Profile
              </Link>
              <Link to="/account-settings" className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                Account Settings
              </Link>
                          <button
                            onClick={handleLogout}
                            className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
                          >
                            Log Out
                          </button>
            </div>
          </>
        )}

        {!username && (
          <p>You are not logged in.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
