import React, { useState } from 'react';
import axios from 'axios';

const SaveArt = ({ userId }) => {
  const [title, setTitle] = useState('');
  const [artData, setArtData] = useState('');
  const [message, setMessage] = useState('');

  const handleSaveArt = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8080/art/save?userId=${userId}`, {
        title,
        artData,
      });
      setMessage(`Art titled "${response.data.title}" saved successfully!`);
    } catch (error) {
      setMessage('Error saving art');
    }
  };

  return (
    <div>
      <h2>Save Pixel Art</h2>
      <form onSubmit={handleSaveArt}>
        <div>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Art Data</label>
          <textarea
            value={artData}
            onChange={(e) => setArtData(e.target.value)}
            required
          />
        </div>
        <button type="submit">Save Art</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default SaveArt;
