import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Navbar from './common/Navbar';
import likeIcon from '../assets/like-icon.png'; // Иконка "лайк"
import likedIcon from '../assets/liked-icon.png'; // Иконка "лайкнутого"

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [publicPalettes, setPublicPalettes] = useState([]);
  const [publicArts, setPublicArts] = useState([]);
  const [activeCategory, setActiveCategory] = useState('palettes');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [likesModalOpen, setLikesModalOpen] = useState(false);
  const [likesUsers, setLikesUsers] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null); // Для отслеживания выбранного арта/палитры
  const modalRef = useRef(null);

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }

    // Загрузка публичных палитр
    axios.get('http://localhost:8080/palettes/public')
      .then(response => setPublicPalettes(response.data))
      .catch(error => console.error('Error fetching public palettes:', error));

    // Загрузка публичных артов
    axios.get('http://localhost:8080/arts/public')
      .then(response => setPublicArts(response.data))
      .catch(error => console.error('Error fetching public arts:', error));
  }, []);

  const handleLike = (itemId, itemType) => {
    const endpoint = itemType === 'palette' ? 'palettes' : 'arts';
    axios.post(`http://localhost:8080/${endpoint}/${itemId}/like`, {}, {
      withCredentials: true,
    })
    .then(() => {
      // Обновляем состояние после лайка
      if (itemType === 'palette') {
        setPublicPalettes(prevState =>
          prevState.map(palette =>
            palette.id === itemId
              ? { ...palette, likes: palette.likes + 1, likedByUser: true }
              : palette
          )
        );
      } else {
        setPublicArts(prevState =>
          prevState.map(art =>
            art.id === itemId
              ? { ...art, likes: art.likes + 1, likedByUser: true }
              : art
          )
        );
      }
    })
    .catch((error) => console.error('Error liking item:', error));
  };

  const handleUnlike = (itemId, itemType) => {
    const endpoint = itemType === 'palette' ? 'palettes' : 'arts';
    axios.post(`http://localhost:8080/${endpoint}/${itemId}/unlike`, {}, {
      withCredentials: true,
    })
    .then(() => {
      // Обновляем состояние после анлайка
      if (itemType === 'palette') {
        setPublicPalettes(prevState =>
          prevState.map(palette =>
            palette.id === itemId
              ? { ...palette, likes: palette.likes - 1, likedByUser: false }
              : palette
          )
        );
      } else {
        setPublicArts(prevState =>
          prevState.map(art =>
            art.id === itemId
              ? { ...art, likes: art.likes - 1, likedByUser: false }
              : art
          )
        );
      }
    })
    .catch((error) => console.error('Error unliking item:', error));
  };

  const handleShowLikes = (itemId, itemType) => {
    const endpoint = itemType === 'palette' ? 'palettes' : 'arts';
    axios.get(`http://localhost:8080/${endpoint}/${itemId}/likes/users`)
      .then(response => {
        setLikesUsers(response.data);
        setSelectedItem(itemId);
        setLikesModalOpen(true);
      })
      .catch(error => console.error('Error fetching users who liked the item:', error));
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setLikesModalOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const openModal = () => {
    setIsModalOpen(true); // Открытие модального окна
  };

  const closeModal = () => {
    setIsModalOpen(false); // Закрытие модального окна
  };

  const handleCreateArt = () => {
    closeModal();
    window.location.href = `/create-art?public=${!isAuthenticated}`;
  };

  const handleCreatePalette = () => {
    closeModal();
    window.location.href = `/create-palette?public=${!isAuthenticated}`;
  };

  return (
    <div className="min-h-screen bg-primary">
      <Navbar
        username={username}
        isAuthenticated={isAuthenticated}
        handleLogout={() => {
          sessionStorage.removeItem('authToken');
          sessionStorage.removeItem('username');
          window.location.href = '/login';
        }}
        onHomePage={true}
        openModal={openModal}
      />

      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-2xl font-bold mb-4">Choose an option</h2>
            <button
              onClick={handleCreateArt}
              className="bg-primary text-secondary py-2 px-4 rounded mb-4 hover:text-primary hover:bg-secondary transition-all w-full"
            >
              Create Art
            </button>
            <button
              onClick={handleCreatePalette}
              className="bg-primary text-secondary py-2 px-4 rounded hover:text-primary hover:bg-secondary transition-all w-full"
            >
              Create Palette
            </button>
            <button
              onClick={closeModal}
              className="mt-4 text-gray-500 hover:text-black"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      <div className="pt-16">
        <div className="container mx-auto max-w-screen-lg py-8 px-8">
          <div className="text-center mb-8">
            <button
              onClick={() => setActiveCategory('palettes')}
              className={`px-4 py-2 mx-2 ${activeCategory === 'palettes' ? 'bg-activeBg text-areasBg' : 'bg-inactiveBg'}`}
            >
              Palettes
            </button>
            <button
              onClick={() => setActiveCategory('arts')}
              className={`px-4 py-2 mx-2 ${activeCategory === 'arts' ? 'bg-activeBg text-areasBg' : 'bg-inactiveBg'}`}
            >
              Arts
            </button>
          </div>

          {activeCategory === 'palettes' && (
            <>
              <h2 className="text-2xl font-bold text-secondary mb-4 text-center">Public Palettes</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-8">
                {publicPalettes.length > 0 ? (
                  publicPalettes.map((palette, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md bg-areasBg">
                      <h3 className="text-lg font-bold mb-2 text-secondary">{palette.name}</h3>
                      <p className="text-sm text-secondary mb-2">by {palette.user?.username ? (
                        <Link to={`/users/${palette.user.username}`} className="underline">
                          {palette.user.username}
                        </Link>
                      ) : 'Unknown'}</p>
                      <div className="grid grid-cols-4 gap-0 mb-4">
                        {palette.colors.map((color, idx) => (
                          <div
                            key={idx}
                            className="w-8 h-8"
                            style={{ backgroundColor: color }}
                          />
                        ))}
                      </div>
                      <div className="flex items-center justify-between">
                        <button
                          onClick={() => palette.likedByUser
                            ? handleUnlike(palette.id, 'palette')
                            : handleLike(palette.id, 'palette')}
                          className="flex items-center"
                        >
                          <img
                            src={palette.likedByUser ? likedIcon : likeIcon}
                            alt="Like"
                            className="h-6 w-6 mr-2"
                          />
                        </button>
                        <span className="text-secondary text-xs">{palette.likes} Likes</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-secondary">No public palettes available.</p>
                )}
              </div>
            </>
          )}

          {activeCategory === 'arts' && (
            <>
              <h2 className="text-2xl font-bold text-secondary mb-4 text-center">Public Arts</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {publicArts.length > 0 ? (
                  publicArts.map((art, index) => (
                    <div key={index} className="flex flex-col items-center rounded-lg shadow-md bg-areasBg">
                      <h3 className="text-lg font-bold mb-2 text-secondary">{art.name}</h3>
                      <p className="text-sm text-secondary mb-2">by {art.user?.username ? (
                        <Link to={`/users/${art.user.username}`} className="underline">
                          {art.user.username}
                        </Link>
                      ) : 'Unknown'}</p>
                      <div
                        className="grid"
                        style={{
                          gridTemplateColumns: `repeat(${art.width}, 1fr)`,
                          gap: '0px',
                          width: `${art.width * 20}px`,
                          height: `${art.height * 20}px`,
                        }}
                      >
                        {art.pixels.map((color, idx) => (
                          <div
                            key={idx}
                            style={{
                              backgroundColor: color,
                              width: '20px',
                              height: '20px',
                            }}
                          />
                        ))}
                      </div>
                      <div className="flex items-center justify-between mt-4">
                        <button
                          onClick={() => art.likedByUser
                            ? handleUnlike(art.id, 'art')
                            : handleLike(art.id, 'art')}
                          className="flex items-center"
                        >
                          <img
                            src={art.likedByUser ? likedIcon : likeIcon}
                            alt="Like"
                            className="h-6 w-6 mr-2"
                          />
                        </button>
                        <span className="text-secondary text-xs">{art.likes} Likes</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <p className="text-secondary">No public arts available.</p>
                )}
              </div>
            </>
          )}
        </div>
      </div>

      {/* Модальное окно с пользователями, лайкнувшими арт или палитру */}
      {likesModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div ref={modalRef} className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full">
            <h2 className="text-2xl font-bold mb-4">Users who liked this item</h2>
            <button onClick={() => setLikesModalOpen(false)} className="absolute top-2 right-2 text-gray-500 hover:text-black">
              Close
            </button>
            <div className="grid grid-cols-3 gap-4">
              {likesUsers && Array.isArray(likesUsers) && likesUsers.length > 0 ? (
                likesUsers.map((user, index) => (
                  <div key={index} className="flex items-center space-x-2">
                    <img
                      src={`http://localhost:8080${user.avatarPath}`}
                      alt={`${user.username}'s avatar`}
                      className="w-10 h-10 rounded-full"
                    />
                    <p className="text-black">{user.username}</p>
                  </div>
                ))
              ) : (
                <p className="text-black">No likes yet.</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
