import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-pixelBg">
      <div className="bg-white shadow-lg rounded-lg p-8">
        <h1 className="text-4xl font-bold text-primary mb-6 text-center">PixApp</h1>

        {isAuthenticated ? (
          <div className="text-center">
            <p className="text-lg mb-4">Logged in as: <span className="font-semibold">{username}</span></p>
            <div className="mb-4">
              <Link to={`/users/${username}`} className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                My Profile
              </Link>
              <Link to="/account-settings" className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                Account Settings
              </Link>
            </div>
            <button
              onClick={handleLogout}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
            >
              Log Out
            </button>
          </div>
        ) : (
          <div className="text-center">
            <p className="text-lg mb-4">You are not logged in.</p>
            <Link to="/login">
              <button className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all mr-4">
                Login
              </button>
            </Link>
            <Link to="/register">
              <button className="bg-secondary text-white py-2 px-4 rounded hover:bg-primary transition-all">
                Register
              </button>
            </Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
