import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const UserProfile = () => {
  const { username } = useParams();
  const [userInfo, setUserInfo] = useState({ username: '', avatarPath: '' });
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:8080/users/${username}`)
      .then(response => {
        setUserInfo(response.data);
      })
      .catch(error => {
        if (error.response && error.response.status === 404) {
          setMessage('User not found');
        } else {
          setMessage('An error occurred while fetching user data');
        }
      });
  }, [username]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-pixelBg">
      <div className="bg-white shadow-lg rounded-lg p-8 max-w-md w-full">
        {message ? (
          <p className="text-red-500 text-center mb-6">{message}</p>
        ) : (
          <>
            <div className="flex justify-center mb-4">
              {userInfo.avatarPath ? (
                <img
                  src={`http://localhost:8080${userInfo.avatarPath}`}
                  alt="User Avatar"
                  className="rounded-full h-32 w-32 object-cover"
                />
              ) : (
                <div className="rounded-full h-32 w-32 bg-gray-300 flex items-center justify-center">
                  No Avatar
                </div>
              )}
            </div>
            <h1 className="text-3xl font-bold text-primary mb-6 text-center">{userInfo.username}</h1>
          </>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
