import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AccountSettings = () => {
  const [username, setUsername] = useState('');
  const [newUsername, setNewUsername] = useState('');
  const [avatar, setAvatar] = useState(null);  // Для хранения текущей аватарки
  const [newAvatar, setNewAvatar] = useState(null);  // Для загрузки новой аватарки
  const [message, setMessage] = useState('');

    useEffect(() => {
      const savedUsername = sessionStorage.getItem('username');
      if (savedUsername) {
        setUsername(savedUsername);
        setNewUsername(savedUsername);

        // Запрашиваем детали пользователя, включая аватар
        axios.get(`http://localhost:8080/users/${savedUsername}`)
          .then(response => {
            const userData = response.data;
            if (userData.avatarPath) {
              setAvatar(userData.avatarPath);  // Устанавливаем аватар, если он есть
            } else {
              console.log("Avatar path is missing from the response");
            }
          })
          .catch(error => console.error('Error fetching user details:', error));
      }
    }, []);


  const handleAvatarChange = (e) => {
    setNewAvatar(e.target.files[0]);  // Устанавливаем новый файл аватарки
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('currentUsername', username);
    formData.append('newUsername', newUsername);
    if (newAvatar) {
      formData.append('avatar', newAvatar);  // Добавляем файл аватарки, если он выбран
    }

    try {
      const token = sessionStorage.getItem('authToken');  // Берём токен из sessionStorage
      const response = await axios.put('http://localhost:8080/users/update-user-details', formData, {
        withCredentials: true,
        headers: {
          'Content-Type': 'multipart/form-data',  // Используем multipart/form-data для отправки файлов
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.status === 200) {
        sessionStorage.setItem('username', newUsername);
        setMessage('User details updated successfully');
        if (response.data.avatarPath) {
          setAvatar(response.data.avatarPath);  // Обновляем аватар после изменения
        }
      } else {
        setMessage('Failed to update user details');
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-pixelBg">
      <div className="bg-white shadow-lg rounded-lg p-8 max-w-md w-full">
        <h1 className="text-3xl font-bold text-primary mb-6 text-center">Account Settings</h1>

        {avatar && (
          <div className="flex justify-center mb-4">
            <img src={"http://localhost:8080${avatar}"} alt="User Avatar" className="rounded-full h-32 w-32 object-cover" />
          </div>
        )}

        {message && (
          <p className="text-center mb-4 text-green-500">{message}</p>
        )}

        <form onSubmit={handleUpdate}>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">New Username</label>
            <input
              type="text"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
              required
            />
          </div>

          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-2">Change Avatar</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleAvatarChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all"
          >
            Update User Details
          </button>
        </form>
      </div>
    </div>
  );
};

export default AccountSettings;
