import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <div className="min-h-screen bg-primary">
      {/* Верхняя панель */}
      <nav className="bg-[#031634] p-2 shadow-lg fixed w-full top-0 z-10">
        <div className="container mx-auto flex justify-between items-center">
          {/* Левая часть панели с логотипом */}
          <div className="flex items-center space-x-4">
            <Link to="/">
              <img src={logo} alt="Logo" className="h-16" />
            </Link>
            <Link to="/">
              <span className="text-white text-2xl font-semibold font-sans">
                Tintify
              </span>
            </Link>
          </div>

          {/* Правая часть панели с выпадающим меню */}
          {isAuthenticated ? (
            <div
              className="relative"
              onMouseOver={() => setMenuOpen(true)}
              onMouseOut={() => setMenuOpen(false)}
            >
              <button className="text-white text-center">
                Welcome, {username}!
              </button>
              {menuOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-lg py-2 z-20">
                  <Link
                    to={`/users/${username}`}
                    className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                  >
                    My Profile
                  </Link>
                  <Link
                    to="/account-settings"
                    className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                  >
                    Account Settings
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200"
                  >
                    Log Out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="flex items-center space-x-4">
              <Link
                to="/login"
                className="text-white hover:text-gray-200 transition-colors"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="text-white hover:text-gray-200 transition-colors"
              >
                Register
              </Link>
            </div>
          )}
        </div>
      </nav>

      {/* Контент */}
      <div className="pt-16">
        <div className="container mx-auto">
          {/* Основной контент здесь */}
          <h1 className="text-center text-3xl font-bold text-white mt-10">
            Welcome to the Home Page!
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Home;
