import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg'; // Импортируем логотип

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <nav className="bg-primary p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <div className="flex items-center space-x-4">
          {/* Логотип */}
          <img src={logo} alt="Logo" className="h-12" />

          {/* Кнопки */}
          {isAuthenticated ? (
            <div className="flex items-center space-x-4">
              <Link to={`/users/${username}`} className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                My Profile
              </Link>
              <Link to="/account-settings" className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                Account Settings
              </Link>
              <button
                onClick={handleLogout}
                className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                Log Out
              </button>
            </div>
          ) : (
            <div className="flex items-center space-x-4">
              <Link to="/login">
                <button className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all">
                  Login
                </button>
              </Link>
              <Link to="/register">
                <button className="bg-secondary text-white py-2 px-4 rounded hover:bg-primary transition-all">
                  Register
                </button>
              </Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Home;
