      {/* Арты пользователя */}
      {activeTab === 'arts' && (
        <>
          <h2 className="text-2xl font-semibold text-secondary mb-4">Arts</h2>
          {arts.length > 0 ? (
            <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {arts.map((art, index) => (
                <div key={index} className="flex flex-col items-center">
                  <h3 className="text-lg font-bold mb-2 text-secondary">{art.name}</h3>
                  <div
                    className="grid"
                    style={{
                      gridTemplateColumns: `repeat(${art.width}, 1fr)`,
                      gap: '0px',
                      width: `${art.width * 20}px`,
                      height: `${art.height * 20}px`,
                    }}
                  >
                    {art.pixels.map((color, idx) => (
                      <div
                        key={idx}
                        style={{
                          backgroundColor: color,
                          width: '100%',
                          height: '100%',
                        }}
                      />
                    ))}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-secondary">This user has no public arts yet.</p>
          )}
        </>
      )}

      {/* Модальное окно с пользователями, лайкнувшими палитру */}
      {likesModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div ref={modalRef} className="bg-white p-6 rounded-lg shadow-lg max-w-3xl w-full">
            <h2 className="text-2xl font-bold mb-4">Users who liked this palette</h2>
            <button onClick={() => setLikesModalOpen(false)} className="absolute top-2 right-2 text-gray-500 hover:text-black">
              Close
            </button>
            <div className="grid grid-cols-3 gap-4">
              {likesUsers && Array.isArray(likesUsers) && likesUsers.length > 0 ? (
                likesUsers.map((user, index) => (
                  <div key={index} className="flex items-center space-x-2">
                    <img
                      src={`http://localhost:8080${user.avatarPath}`}
                      alt={`${user.username}'s avatar`}
                      className="w-10 h-10 rounded-full"
                    />
                    <p className="text-black">{user.username}</p>
                  </div>
                ))
              ) : (
                <p className="text-black">No likes yet.</p>
              )}
            </div>
          </div>
        </div>
      )}

export default UserProfile;
