import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';
import Navbar from './common/Navbar';
import likeIcon from '../assets/like-icon.png'; // Иконка "лайк"
import likedIcon from '../assets/liked-icon.png'; // Иконка "лайкнутого"

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [publicArts, setPublicArts] = useState([]); // Публичные арты
  const [publicPalettes, setPublicPalettes] = useState([]); // Публичные палитры
  const [likedPalettes, setLikedPalettes] = useState([]); // Лайкнутые палитры
  const [likedArts, setLikedArts] = useState([]); // Лайкнутые арты

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }

    // Получение публичных артов и палитр
    axios.get('http://localhost:8080/arts/public')
      .then(response => setPublicArts(response.data))
      .catch(error => console.error('Error fetching public arts:', error));

    axios.get('http://localhost:8080/palettes/public')
      .then(response => setPublicPalettes(response.data))
      .catch(error => console.error('Error fetching public palettes:', error));

    if (savedUsername) {
      axios.get(`http://localhost:8080/users/${savedUsername}/likes/arts`, { withCredentials: true })
        .then(response => setLikedArts(response.data))
        .catch(error => console.error('Error fetching liked arts:', error));

      axios.get(`http://localhost:8080/users/${savedUsername}/likes/palettes`, { withCredentials: true })
        .then(response => setLikedPalettes(response.data))
        .catch(error => console.error('Error fetching liked palettes:', error));
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  let dropdownTimer;

  const handleMouseEnter = () => {
    clearTimeout(dropdownTimer);
    setIsDropdownOpen(true);
  };

  const handleMouseLeave = () => {
    dropdownTimer = setTimeout(() => {
      setIsDropdownOpen(false);
    }, 300);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleCreateArt = () => {
    closeModal();
    window.location.href = `/create-art?public=${!isAuthenticated}`;
  };

  const handleCreatePalette = () => {
    closeModal();
    window.location.href = `/create-palette?public=${!isAuthenticated}`;
  };

  const handleLike = (id, type) => {
    const url = `http://localhost:8080/${type}/${id}/like`;
    axios.post(url, {}, { withCredentials: true })
      .then(() => {
        if (type === 'arts') {
          setPublicArts(publicArts.map(art =>
            art.id === id ? { ...art, likes: art.likes + 1 } : art
          ));
        } else if (type === 'palettes') {
          setPublicPalettes(publicPalettes.map(palette =>
            palette.id === id ? { ...palette, likes: palette.likes + 1 } : palette
          ));
        }
      })
      .catch(error => console.error('Error liking item:', error));
  };

  return (
    <div className="min-h-screen bg-primary">
      {/* Верхняя панель */}
      <Navbar
        username={username}
        isAuthenticated={isAuthenticated}
        handleLogout={handleLogout}
        onHomePage={true}
        openModal={openModal}
      />

      {/* Модальное окно */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-2xl font-bold mb-4">Choose an option</h2>
            <button
              onClick={handleCreateArt}
              className="bg-primary text-secondary py-2 px-4 rounded mb-4 hover:text-primary hover:bg-secondary transition-all w-full"
            >
              Create Art
            </button>
            <button
              onClick={handleCreatePalette}
              className="bg-primary text-secondary py-2 px-4 rounded hover:text-primary hover:bg-secondary transition-all w-full"
            >
              Create Palette
            </button>
            <button
              onClick={closeModal}
              className="mt-4 text-gray-500 hover:text-black"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Контент */}
      <div className="pt-16 container mx-auto">
        <h1 className="text-center text-3xl font-bold text-secondary mb-8">Explore Artworks and Palettes</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Публичные арты */}
          <div>
            <h2 className="text-2xl text-white mb-4">Arts</h2>
            {publicArts.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {publicArts.map((art, index) => (
                  <div key={index} className="p-4 bg-secondary rounded-lg shadow-md">
                    <h3 className="text-lg font-bold text-white mb-2">{art.name}</h3>
                    <div className="grid" style={{ gridTemplateColumns: `repeat(${art.width}, 1fr)`, gap: '0' }}>
                      {art.pixels.map((color, idx) => (
                        <div key={idx} style={{ backgroundColor: color, width: '20px', height: '20px' }} />
                      ))}
                    </div>
                    <div className="flex items-center justify-between mt-2">
                      <p className="text-white">by <Link to={`/users/${art.user.username}`} className="underline">{art.user.username}</Link></p>
                      <button onClick={() => handleLike(art.id, 'arts')}>
                        <img
                          src={likedArts.some(a => a.id === art.id) ? likedIcon : likeIcon}
                          alt="Like"
                          className="h-6 w-6"
                        />
                        <span className="text-white text-xs ml-1">{art.likes} Likes</span>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-white">No public arts available.</p>
            )}
          </div>

          {/* Публичные палитры */}
          <div>
            <h2 className="text-2xl text-white mb-4">Palettes</h2>
            {publicPalettes.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {publicPalettes.map((palette, index) => (
                  <div key={index} className="p-4 bg-secondary rounded-lg shadow-md">
                    <h3 className="text-lg font-bold text-white mb-2">{palette.name}</h3>
                    <div className="grid grid-cols-4 gap-1">
                      {palette.colors.map((color, idx) => (
                        <div key={idx} style={{ backgroundColor: color, width: '20px', height: '20px' }} />
                      ))}
                    </div>
                    <div className="flex items-center justify-between mt-2">
                      <p className="text-white">by <Link to={`/users/${palette.user.username}`} className="underline">{palette.user.username}</Link></p>
                      <button onClick={() => handleLike(palette.id, 'palettes')}>
                        <img
                          src={likedPalettes.some(p => p.id === palette.id) ? likedIcon : likeIcon}
                          alt="Like"
                          className="h-6 w-6"
                        />
                        <span className="text-white text-xs ml-1">{palette.likes} Likes</span>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-white">No public palettes available.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
