import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../../assets/logo.jpg';

const Navbar = ({ showSaveButton, onSaveClick, openModal }) => {
  const isAuthenticated = sessionStorage.getItem('authToken');
  const username = sessionStorage.getItem('username');
  const location = useLocation();

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  return (
    <nav className="bg-[#031634] p-4 shadow-md fixed w-full top-0 z-10">
      <div className="container mx-auto flex justify-between items-center">
        {/* Левая часть панели с логотипом и названием */}
        <div className="flex items-center space-x-4">
          <Link to="/">
            <img src={logo} alt="Logo" className="h-12" />
          </Link>
          <Link to="/">
            <span className="text-white text-2xl font-semibold">Tintify</span>
          </Link>
        </div>

        {/* Правая часть панели */}
        <div className="flex items-center space-x-4">
          {showSaveButton && (
            <button
              onClick={onSaveClick}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
            >
              Save
            </button>
          )}

          {isAuthenticated ? (
            <>
              <span className="text-white">Welcome, {username}!</span>
              <Link
                to={`/users/${username}`}
                className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                My Profile
              </Link>
              <Link
                to="/account-settings"
                className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                Account Settings
              </Link>
              <button
                onClick={handleLogout}
                className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                Log Out
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
              >
                Register
              </Link>
            </>
          )}

          {/* Кнопка "Create" отображается только на главной странице */}
          {(location.pathname === '/home' || location.pathname === '/') && (
            <button
              onClick={openModal}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-transform duration-200 hover:-translate-y-1"
            >
              Create
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
