import React, { useState, useRef, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import Navbar from './common/Navbar'; // Верхняя панель

const CreateArt = () => {
  const [width, setWidth] = useState(16); // Ширина матрицы
  const [height, setHeight] = useState(16); // Высота матрицы
  const [pixels, setPixels] = useState(Array(width * height).fill('#ffffff')); // Изначальные цвета
  const [currentColor, setCurrentColor] = useState('#000000'); // Текущий цвет
  const [pickerVisible, setPickerVisible] = useState(false); // Видимость цветового колеса
  const [selectedPixel, setSelectedPixel] = useState(null); // Выбранный пиксель
  const [position, setPosition] = useState({ top: 0, left: 0 }); // Позиция цветового колеса

  const pickerRef = useRef(null);

  // Закрытие color picker при клике вне его области
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        setPickerVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Изменение цвета выбранного квадрата
  const handleColorChange = (color) => {
    const newPixels = [...pixels];
    newPixels[selectedPixel] = color.hex;
    setPixels(newPixels);
    setCurrentColor(color.hex);
  };

  // Обработка клика на пиксель и отображение color picker рядом
  const handlePixelClick = (index, event) => {
    setSelectedPixel(index);
    setCurrentColor(pixels[index]);
    setPickerVisible(true);

    const rect = event.target.getBoundingClientRect();
    setPosition({
      top: rect.top + window.scrollY + rect.height, // Появляется ниже квадрата
      left: rect.left + window.scrollX, // Появляется на месте клика
    });
  };

  // Обработка изменения ширины
  const handleWidthChange = (e) => {
    const newWidth = Math.max(2, Math.min(32, Number(e.target.value))); // Ограничение
    setWidth(newWidth);
    setPixels(Array(newWidth * height).fill('#ffffff')); // Пересчёт пикселей
  };

  // Обработка изменения высоты
  const handleHeightChange = (e) => {
    const newHeight = Math.max(2, Math.min(32, Number(e.target.value))); // Ограничение
    setHeight(newHeight);
    setPixels(Array(width * newHeight).fill('#ffffff')); // Пересчёт пикселей
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar />
      <div className="container mx-auto py-8">
        <h1 className="text-center text-white text-3xl font-bold mb-6">Create Your Pixel Art</h1>

        {/* Селекторы высоты и ширины */}
        <div className="flex justify-center mb-6 space-x-4">
          <div>
            <label className="text-white mr-2">Width:</label>
            <input
              type="number"
              value={width}
              onChange={handleWidthChange}
              min="2"
              max="32"
              className="px-2 py-1 rounded text-black"
            />
          </div>
          <div>
            <label className="text-white mr-2">Height:</label>
            <input
              type="number"
              value={height}
              onChange={handleHeightChange}
              min="2"
              max="32"
              className="px-2 py-1 rounded text-black"
            />
          </div>
        </div>

        {/* Отображение матрицы пикселей */}
        <div
          className="grid"
          style={{
            gridTemplateColumns: `repeat(${width}, 1fr)`, // Динамическая ширина
            width: `${width * 24}px`, // Ширина контейнера матрицы
          }}
        >
          {pixels.map((color, index) => (
            <div
              key={index}
              className="w-6 h-6 md:w-8 md:h-8 cursor-pointer border" // Убираем закругление и отступы
              style={{ backgroundColor: color }}
              onClick={(e) => handlePixelClick(index, e)}
            />
          ))}
        </div>

        {/* Цветовое колесо */}
        {pickerVisible && (
          <div
            ref={pickerRef}
            style={{
              position: 'absolute',
              top: `${position.top}px`,
              left: `${position.left}px`,
              zIndex: 1000,
            }}
          >
            <ChromePicker color={currentColor} onChangeComplete={handleColorChange} />
          </div>
        )}
      </div>
    </div>
  );
};

export default CreateArt;
