import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import Navbar from './common/Navbar';
import likeIcon from '../assets/like-icon.png';  // Иконка "лайк"
import likedIcon from '../assets/liked-icon.png'; // Иконка "лайкнутого"

const UserProfile = () => {
  const { username } = useParams();
  const [user, setUser] = useState(null);
  const [palettes, setPalettes] = useState([]);
  const [likedPalettes, setLikedPalettes] = useState([]);
  const [arts, setArts] = useState([]);  // Новая переменная для публичных артов
  const [avatar, setAvatar] = useState(null);
  const [currentUser, setCurrentUser] = useState('');
  const [activeTab, setActiveTab] = useState('palettes'); // Вкладки

  useEffect(() => {
    // Загружаем данные пользователя
    axios.get(`http://localhost:8080/users/${username}`)
      .then(response => {
        setUser(response.data);
        setAvatar(`http://localhost:8080${response.data.avatarPath}`);
      })
      .catch(error => console.error('Error fetching user data:', error));

    // Загружаем публичные палитры пользователя
    axios.get(`http://localhost:8080/palettes/user/${username}`)
      .then(response => {
        setPalettes(response.data);
      })
      .catch(error => console.error('Error fetching palettes:', error));

    // Загружаем палитры, которые лайкнул пользователь
    axios.get(`http://localhost:8080/palettes/user/${username}/likes`)
      .then(response => {
        setLikedPalettes(response.data);
      })
      .catch(error => console.error('Error fetching liked palettes:', error));

    // Загружаем публичные арты пользователя
    axios.get(`http://localhost:8080/art/user/${username}`)
      .then(response => {
        setArts(response.data);
      })
      .catch(error => console.error('Error fetching arts:', error));

    // Получаем текущего залогиненного пользователя
    const loggedInUser = sessionStorage.getItem('username');
    setCurrentUser(loggedInUser);
  }, [username]);

  if (!user) {
    return <div>Loading...</div>;
  }

  const handleLike = (paletteId) => {
    axios.post(`http://localhost:8080/palettes/${paletteId}/like`, {}, {
      withCredentials: true
    })
      .then(() => {
        // Обновляем лайки
        setPalettes(palettes.map(palette =>
          palette.id === paletteId ? { ...palette, likes: palette.likes + 1 } : palette
        ));
      })
      .catch((error) => console.error('Error liking palette:', error));
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar />
      <div className="container mx-auto py-8 text-center">
        <img src={avatar} alt="User Avatar" className="rounded-full h-32 w-32 object-cover mx-auto mb-4" />
        <h1 className="text-3xl font-bold text-white">{user.username}</h1>

        {/* Вкладки */}
        <div className="mt-8 flex justify-center space-x-8">
          <button
            onClick={() => setActiveTab('palettes')}
            className={`px-4 py-2 ${activeTab === 'palettes' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
          >
            Palettes
          </button>
          <button
            onClick={() => setActiveTab('liked-palettes')}
            className={`px-4 py-2 ${activeTab === 'liked-palettes' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
          >
            Liked Palettes
          </button>
          <button
            onClick={() => setActiveTab('arts')}
            className={`px-4 py-2 ${activeTab === 'arts' ? 'bg-secondary text-white' : 'bg-gray-200'}`}
          >
            Arts
          </button>
        </div>

        {/* Содержимое вкладок */}
        <div className="mt-8">
          {/* Палитры пользователя */}
          {activeTab === 'palettes' && (
            <>
              <h2 className="text-2xl font-semibold text-white mb-4">Palettes</h2>
              {palettes.length > 0 ? (
                <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {palettes.map((palette, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                      <h3 className="text-lg font-bold mb-2 text-white">{palette.name}</h3>
                      <div className="grid grid-cols-4 gap-2 mb-4">
                        {palette.colors.map((color, idx) => (
                          <div
                            key={idx}
                            className="w-8 h-8 md:w-10 md:h-10 lg:w-12 lg:h-12 rounded border-2 border-gray-300"
                            style={{ backgroundColor: color }}
                          />
                        ))}
                      </div>
                      <div className="flex items-center justify-between">
                        <button
                          onClick={() => handleLike(palette.id)}
                          disabled={currentUser === username}
                          className={`flex items-center ${currentUser === username ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105 transition-transform'}`}
                        >
                          <img
                            src={likedPalettes.some(p => p.id === palette.id) ? likedIcon : likeIcon}
                            alt="Like"
                            className="h-6 w-6 mr-2"
                          />
                          <span className="text-white text-xs">{palette.likes} Likes</span>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-white">This user has no public palettes yet.</p>
              )}
            </>
          )}

          {/* Лайкнутые палитры */}
          {activeTab === 'liked-palettes' && (
            <>
              <h2 className="text-2xl font-semibold text-white mb-4">Liked Palettes</h2>
              {likedPalettes.length > 0 ? (
                <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {likedPalettes.map((palette, index) => (
                    <div key={index} className="p-4 rounded-lg shadow-md" style={{ backgroundColor: '#284065' }}>
                      <h3 className="text-lg font-bold mb-2 text-white">{palette.name}</h3>
                      <p className="text-sm text-white">by <Link to={`/users/${palette.user.username}`} className="underline">{palette.user.username}</Link></p>
                      <div className="grid grid-cols-4 gap-2 mb-4">
                        {palette.colors.map((color, idx) => (
                          <div
                            key={idx}
                            className="w-8 h-8 md:w-10 md:h-10 lg:w-12 lg:h-12 rounded border-2 border-gray-300"
                            style={{ backgroundColor: color }}
                          />
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-white">This user has not liked any public palettes yet.</p>
              )}
            </>
          )}

          {/* Арты пользователя */}
                  <div className="mt-8">
                    <h2 className="text-2xl font-semibold text-white mb-4">Arts</h2>
                    {publicArts.length > 0 ? (
                      <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                        {publicArts.map((art, index) => (
                          <div key={index} className="flex flex-col items-center">
                            <h3 className="text-lg font-bold mb-2 text-white">{art.name}</h3>
                            {/* Отображение матрицы арта */}
                            <div
                              className="grid"
                              style={{
                                gridTemplateColumns: `repeat(${art.width}, ${400 / art.width}px)`, // Используем width для пропорции
                                gridTemplateRows: `repeat(${art.height}, ${400 / art.height}px)`, // Используем height для пропорции
                                gap: '0px', // Убираем отступы между пикселями
                              }}
                            >
                              {art.pixels.map((color, idx) => (
                                <div
                                  key={idx}
                                  style={{
                                    backgroundColor: color,
                                    width: `${400 / art.width}px`,  // Размер каждого пикселя по ширине
                                    height: `${400 / art.height}px`,  // Размер каждого пикселя по высоте
                                  }}
                                />
                              ))}
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p className="text-white">This user has no public arts yet.</p>
                    )}
                  </div>
                </div>
      </div>
    </div>
  );
};

export default UserProfile;
