import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Navbar from './common/Navbar';

const CreatePalette = () => {
  const [colors, setColors] = useState(Array(4).fill('#ffffff'));
  const [squareCount, setSquareCount] = useState(4);
  const [isPublic, setIsPublic] = useState(true); // Публичность палитры по умолчанию
  const location = useLocation();

  // Получаем параметр из URL для настройки публичности палитры
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const isPublicParam = params.get('public');
    if (isPublicParam === 'false') {
      setIsPublic(false); // Если не аутентифицирован, то публично
    }
  }, [location.search]);

  const handleSquareCountChange = (e) => {
    const count = Math.min(32, Math.max(2, Number(e.target.value)));
    setSquareCount(count);
    setColors(Array(count).fill('#ffffff'));
  };

  const handleSaveClick = () => {
    const paletteData = {
      colors,
      isPublic
    };
    // Логика сохранения палитры
    console.log('Saving palette:', paletteData);
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar showSaveButton={true} onSaveClick={handleSaveClick} />
      <div className="container mx-auto py-8">
        <h1 className="text-center text-white text-3xl font-bold mb-6">Create Your Palette</h1>

        {/* Селектор количества цветов */}
        <div className="flex justify-center mb-6">
          <label className="text-white mr-4">Number of Colors:</label>
          <input
            type="number"
            value={squareCount}
            onChange={handleSquareCountChange}
            min="2"
            max="32"
            className="px-2 py-1 rounded text-black"
          />
        </div>

        {/* Отображение квадратов */}
        <div className={`grid grid-cols-${Math.ceil(Math.sqrt(squareCount))} gap-4`}>
          {colors.map((color, index) => (
            <div
              key={index}
              className="w-20 h-20 rounded-lg cursor-pointer"
              style={{ backgroundColor: color }}
            />
          ))}
        </div>

        {/* Публичность палитры */}
        <div className="mt-6 flex justify-center">
          <label className="text-white mr-4">Make this palette public:</label>
          {!isPublic ? (
            <input
              type="checkbox"
              checked={isPublic}
              onChange={(e) => setIsPublic(e.target.checked)}
              className="form-checkbox"
            />
          ) : (
            <span className="text-white">This palette will be public</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default CreatePalette;
