import React, { useState, useRef, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import Navbar from './common/Navbar'; // Верхняя панель

const CreateArt = () => {
  const [width, setWidth] = useState(16); // Ширина матрицы пиксельного рисунка
  const [height, setHeight] = useState(16); // Высота матрицы пиксельного рисунка
  const [pixels, setPixels] = useState(Array(width * height).fill('#ffffff')); // Изначальные цвета пикселей

  const [paletteWidth, setPaletteWidth] = useState(2); // Ширина палитры
  const [paletteHeight, setPaletteHeight] = useState(8); // Высота палитры
  const [paletteColors, setPaletteColors] = useState(Array(paletteWidth * paletteHeight).fill('#ffffff')); // Изначальные цвета палитры

  const [currentColor, setCurrentColor] = useState('#000000'); // Текущий цвет
  const [pickerVisible, setPickerVisible] = useState(false); // Видимость цветового колеса
  const [selectedPixel, setSelectedPixel] = useState(null); // Выбранный пиксель или цвет из палитры
  const [position, setPosition] = useState({ top: 0, left: 0 }); // Позиция цветового колеса

  const pickerRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        setPickerVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Изменение цвета выбранного квадрата
  const handleColorChange = (color) => {
    if (selectedPixel !== null) {
      if (selectedPixel < paletteColors.length) {
        const newColors = [...paletteColors];
        newColors[selectedPixel] = color.hex;
        setPaletteColors(newColors);
      } else {
        const newPixels = [...pixels];
        newPixels[selectedPixel - paletteColors.length] = color.hex;
        setPixels(newPixels);
      }
    }
    setCurrentColor(color.hex);
  };

  // Обработка клика на пиксель или цвет из палитры
  const handleSquareClick = (index, event, isPalette) => {
    setSelectedPixel(isPalette ? index : index + paletteColors.length);
    setCurrentColor(isPalette ? paletteColors[index] : pixels[index]);
    setPickerVisible(true);

    const rect = event.target.getBoundingClientRect();
    setPosition({
      top: rect.top + window.scrollY + rect.height, // Появляется ниже квадрата
      left: rect.left + window.scrollX, // Появляется на месте клика
    });
  };

  const handleWidthChange = (e) => {
    const newWidth = Math.max(2, Math.min(32, Number(e.target.value)));
    setWidth(newWidth);
    setPixels(Array(newWidth * height).fill('#ffffff'));
  };

  const handleHeightChange = (e) => {
    const newHeight = Math.max(2, Math.min(32, Number(e.target.value)));
    setHeight(newHeight);
    setPixels(Array(width * newHeight).fill('#ffffff'));
  };

  // Увеличение размера палитры
  const increasePaletteWidth = () => {
    const newPaletteWidth = Math.min(8, paletteWidth + 1);
    setPaletteWidth(newPaletteWidth);
    setPaletteColors(Array(newPaletteWidth * paletteHeight).fill('#ffffff'));
  };

  const increasePaletteHeight = () => {
    const newPaletteHeight = Math.min(16, paletteHeight + 1);
    setPaletteHeight(newPaletteHeight);
    setPaletteColors(Array(paletteWidth * newPaletteHeight).fill('#ffffff'));
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar />
      <div className="container mx-auto py-8 text-center">
        <h1 className="text-white text-3xl font-bold mb-6">Create Your Pixel Art</h1>

        {/* Настройки размеров для палитры и пикселей */}
        <div className="flex justify-center space-x-16 mb-6">
          {/* Настройки для пиксельного рисунка */}
          <div>
            <h2 className="text-white text-xl mb-4">Pixel Art Settings</h2>
            <div className="flex justify-center space-x-4">
              <div>
                <label className="text-white mr-2">Width:</label>
                <input
                  type="number"
                  value={width}
                  onChange={handleWidthChange}
                  min="2"
                  max="32"
                  className="px-2 py-1 rounded text-black"
                />
              </div>
              <div>
                <label className="text-white mr-2">Height:</label>
                <input
                  type="number"
                  value={height}
                  onChange={handleHeightChange}
                  min="2"
                  max="32"
                  className="px-2 py-1 rounded text-black"
                />
              </div>
            </div>
          </div>

          {/* Настройки для палитры */}
          <div>
            <h2 className="text-white text-xl mb-4">Palette Settings</h2>
            <div className="flex flex-col items-center">
              <div className="flex space-x-2">
                <button
                  onClick={increasePaletteWidth}
                  className="bg-secondary text-white px-4 py-2 rounded hover:bg-secondary-dark"
                >
                  + Width
                </button>
                <button
                  onClick={increasePaletteHeight}
                  className="bg-secondary text-white px-4 py-2 rounded hover:bg-secondary-dark"
                >
                  + Height
                </button>
              </div>
            </div>
          </div>
        </div>

        <div className="flex justify-center space-x-16">
          {/* Палитра слева */}
          <div>
            <h2 className="text-white text-xl mb-4">Palette</h2>
            <div
              className="grid"
              style={{
                gridTemplateColumns: `repeat(${paletteWidth}, 1fr)`,
                gap: '0px',
              }}
            >
              {paletteColors.map((color, index) => (
                <div
                  key={index}
                  className="w-10 h-10 md:w-12 md:h-12 cursor-pointer border"
                  style={{ backgroundColor: color }}
                  onClick={(e) => handleSquareClick(index, e, true)}
                />
              ))}
            </div>
          </div>

          {/* Матрица пикселей */}
          <div className="flex flex-col items-center">
            <h2 className="text-white text-xl mb-4">Pixel Art</h2>

            <div
              className="grid"
              style={{
                gridTemplateColumns: `repeat(${width}, 1fr)`,
                gap: '0px',
              }}
            >
              {pixels.map((color, index) => (
                <div
                  key={index}
                  className="w-6 h-6 md:w-8 md:h-8 cursor-pointer border"
                  style={{ backgroundColor: color }}
                  onClick={(e) => handleSquareClick(index, e, false)}
                />
              ))}
            </div>
          </div>
        </div>

        {/* Цветовое колесо */}
        {pickerVisible && (
          <div
            ref={pickerRef}
            style={{
              position: 'absolute',
              top: `${position.top}px`,
              left: `${position.left}px`,
              zIndex: 1000,
            }}
          >
            <ChromePicker color={currentColor} onChangeComplete={handleColorChange} />
          </div>
        )}
      </div>
    </div>
  );
};

export default CreateArt;
