import React, { useState, useEffect, useRef } from 'react';
import { ChromePicker } from 'react-color';
import Navbar from './common/Navbar';
import axios from 'axios';
import { generatePalette } from './colorUtils'; // Функция для генерации цветовых схем

const CreatePalette = () => {
  const [colors, setColors] = useState(Array(4).fill('#ffffff'));  // Изначальные цвета квадратов
  const [currentColor, setCurrentColor] = useState('#ffffff');  // Текущий цвет
  const [pickerVisible, setPickerVisible] = useState(false);  // Видимость колеса
  const [selectedSquare, setSelectedSquare] = useState(null);  // Выбранный квадрат
  const [squareCount, setSquareCount] = useState(4);  // Количество квадратов
  const [position, setPosition] = useState({ top: 0, left: 0 });  // Позиция color picker

  const [isSaveModalOpen, setIsSaveModalOpen] = useState(false);  // Управление модальным окном
  const [paletteName, setPaletteName] = useState('');  // Название палитры
  const [isPublic, setIsPublic] = useState(true);  // Приватная или публичная палитра
  const [isCheckboxDisabled, setIsCheckboxDisabled] = useState(false);  // Управление доступностью чекбокса
  const [selectedScheme, setSelectedScheme] = useState('monochromatic');  // Выбранная цветовая схема

  const pickerRef = useRef(null);

  // Симуляция аутентификации пользователя
  const isAuthenticated = sessionStorage.getItem('authToken') ? true : false;

  useEffect(() => {
    if (!isAuthenticated) {
      setIsPublic(true);  // Устанавливаем палитру публичной для неаутентифицированных пользователей
      setIsCheckboxDisabled(true);  // Делаем чекбокс неактивным
    }
  }, [isAuthenticated]);

  // Закрытие color picker при клике вне его области
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (pickerRef.current && !pickerRef.current.contains(event.target)) {
        setPickerVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Обновление цвета выбранного квадрата
  const handleColorChange = (color) => {
    const newColors = [...colors];
    newColors[selectedSquare] = color.hex;
    setColors(newColors);
    setCurrentColor(color.hex);
  };

  // Обработка изменения количества квадратов
  const handleSquareCountChange = (e) => {
    const count = Math.min(32, Math.max(2, Number(e.target.value)));  // Ограничение от 2 до 32
    setSquareCount(count);
    setColors(Array(count).fill('#ffffff'));
  };

  // Обработчик выбора квадрата и отображение color picker рядом с курсором
  const handleSquareClick = (index, event) => {
    setSelectedSquare(index);
    setCurrentColor(colors[index]);
    setPickerVisible(true);
    const rect = event.target.getBoundingClientRect();
    setPosition({
      top: rect.top + window.scrollY + rect.height,  // Появляется ниже квадрата
      left: rect.left + window.scrollX,  // Появляется на месте клика
    });
  };

  // Открытие модального окна для сохранения палитры
  const handleSaveClick = () => {
    setIsSaveModalOpen(true);
  };

  // Закрытие модального окна
  const closeModal = () => {
    setIsSaveModalOpen(false);
  };

  // Обработка сохранения палитры
  const handleSavePalette = async () => {
    try {
      const paletteData = {
        name: paletteName,
        colors: colors,
        isPublic: isPublic
      };

      const token = sessionStorage.getItem('authToken');  // Используем токен для аутентификации

      await axios.post('http://localhost:8080/palettes/create', paletteData, {
        withCredentials: true,
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      closeModal();
    } catch (error) {
      console.error('Error saving palette:', error);
    }
  };

  const handleSchemeChange = (e) => {
    setSelectedScheme(e.target.value);
    const newPalette = generatePalette(e.target.value, currentColor, squareCount);
    setColors(newPalette);
  };

  return (
    <div className="min-h-screen bg-primary pt-16">
      <Navbar showSaveButton={true} onSaveClick={handleSaveClick} isHomepage={false}/>
      <div className="container mx-auto py-8">
        <h1 className="text-center text-secondary text-3xl font-bold mb-6">Create your palette</h1>

        <div className="flex justify-center mb-6">
          <label className="text-secondary mr-4">Choose a color scheme:</label>
          <select
            value={selectedScheme}
            onChange={handleSchemeChange}
            className="px-2 py-1 rounded text-black"
          >
            <option value="monochromatic">Monochromatic</option>
            <option value="analogous">Analogous</option>
            <option value="complementary">Complementary</option>
            <option value="split_complementary">Split Complementary</option>
            <option value="triadic">Triadic</option>
            <option value="square">Square</option>
            <option value="rectangle">Rectangle (Tetradic)</option>
          </select>
        </div>

        <div className="flex justify-center mb-6">
          <label className="text-secondary mr-4">Number of colors:</label>
          <input
            type="number"
            value={squareCount}
            onChange={handleSquareCountChange}
            min="2"
            max="24"
            className="px-2 py-1 rounded text-black"
          />
        </div>

        <div className="flex flex-wrap justify-center">
          {colors.map((color, index) => (
            <div
              key={index}
              className="w-40 h-40 md:w-32 md:h-32 lg:w-40 lg:h-40 cursor-pointer transition-transform transform hover:scale-105"
              style={{ backgroundColor: color }}
              onClick={(e) => handleSquareClick(index, e)}
            />
          ))}
        </div>

        {pickerVisible && (
          <div
            ref={pickerRef}
            style={{
              position: 'absolute',
              top: `${position.top}px`,
              left: `${position.left}px`,
              zIndex: 1000,
            }}
          >
            <ChromePicker color={currentColor} onChangeComplete={handleColorChange} />
          </div>
        )}
      </div>

      {/* Модальное окно сохранения */}
      {isSaveModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold mb-4">Save palette</h2>

            <div className="mb-4">
              <label className="block mb-2">Palette name:</label>
              <input
                type="text"
                value={paletteName}
                onChange={(e) => setPaletteName(e.target.value)}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
                required
              />
            </div>

            <div className="mb-4">
              <label className="inline-flex items-center">
                <input
                  type="checkbox"
                  checked={isPublic}
                  onChange={() => setIsPublic(!isPublic)}
                  className="mr-2"
                  disabled={isCheckboxDisabled}  // Делаем чекбокс неактивным, если пользователь не аутентифицирован
                />
                Make this palette public
              </label>
            </div>

            <div className="flex justify-end space-x-4">
              <button
                onClick={closeModal}
                className="px-4 py-2 bg-gray-400 text-secondary rounded hover:bg-gray-500"
              >
                Cancel
              </button>
              <button
                onClick={handleSavePalette}
                className="px-4 py-2 bg-primary text-secondary rounded hover:bg-secondary"
              >
                Save palette
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePalette;
