import React, { useEffect, useState } from 'react';
import Navbar from './common/Navbar';

const Home = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false); // Модальное окно

  useEffect(() => {
    const token = sessionStorage.getItem('authToken');
    const savedUsername = sessionStorage.getItem('username');

    if (token && savedUsername) {
      setIsAuthenticated(true);
      setUsername(savedUsername);
    }
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('username');
    window.location.href = '/#/login';
  };

  // Управление модальным окном
  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const handleCreateArt = () => {
    window.location.href = '/#/create-art';
    closeModal();
  };

  const handleCreatePalette = () => {
    window.location.href = '/#/create-palette';
    closeModal();
  };

  return (
    <div className="min-h-screen bg-primary">
      {/* Верхняя панель */}
      <Navbar openModal={openModal} />

      {/* Контент страницы */}
      <div className="container mx-auto pt-16">
        <h1 className="text-center text-3xl font-bold text-white mt-10">Welcome to the Home Page!</h1>
      </div>

      {/* Модальное окно для Create */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-2xl font-bold mb-4">Choose an option</h2>
            <button
              onClick={handleCreateArt}
              className="bg-primary text-white py-2 px-4 rounded mb-4 hover:bg-secondary transition-all w-full"
            >
              Create Art
            </button>
            <button
              onClick={handleCreatePalette}
              className="bg-primary text-white py-2 px-4 rounded hover:bg-secondary transition-all w-full"
            >
              Create Palette
            </button>
            <button
              onClick={closeModal}
              className="mt-4 text-gray-500 hover:text-black"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
